http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "examples": {
        "Patch user example": {
          "description": "Newly patched user",
          "summary": "Patch user example",
          "value": {
            "Operations": [
              {
                "op": "replace",
                "path": "title",
                "value": "Manager"
              }
            ],
            "schemas": [
              "urn:ietf:params:scim:api:messages:2.0:PatchOp"
            ]
          }
        }
      },
      "schema": {
        "properties": {
          "Operations": {
            "description": "A list of SCIM patch operations to perform.\n\nSee the [SCIM PATCH protocol](https://datatracker.ietf.org/doc/html/rfc7644#section-3.5.2) for details.",
            "items": {
              "properties": {
                "op": {
                  "enum": [
                    "add",
                    "replace",
                    "remove"
                  ],
                  "type": "string"
                },
                "path": {
                  "description": "Path for a [SCIM user](/api/scim-overview#scim-user-objects) field.",
                  "type": "string"
                },
                "value": {
                  "anyOf": [
                    {
                      "type": "string"
                    },
                    {}
                  ],
                  "description": "Replacment value. Not used when performing the `'remove'` operation."
                }
              },
              "required": [
                "op",
                "path"
              ],
              "type": "object"
            },
            "type": "array"
          },
          "schemas": {
            "description": "A list of schemas, including at least SCIM's PatchOp schema.",
            "items": {
              "type": "string"
            },
            "type": "array"
          }
        },
        "required": [
          "schemas",
          "Operations"
        ],
        "type": "object"
      }
    }
  }
}
