http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "properties": {
          "collaborators": {
            "description": "This endpoint accepts two types of collaborators: **individual user** and **group**. This route currently supports adding only one collaborator at a time.",
            "items": {
              "anyOf": [
                {
                  "properties": {
                    "group": {
                      "not": {}
                    },
                    "permissionLevel": {
                      "enum": [
                        "read",
                        "comment",
                        "edit"
                      ],
                      "type": "string"
                    },
                    "user": {
                      "properties": {
                        "id": {
                          "description": "A user ID",
                          "type": "string"
                        }
                      },
                      "required": [
                        "id"
                      ],
                      "type": "object"
                    }
                  },
                  "required": [
                    "permissionLevel",
                    "user"
                  ],
                  "type": "object"
                },
                {
                  "properties": {
                    "group": {
                      "properties": {
                        "id": {
                          "description": "A user group ID",
                          "type": "string"
                        }
                      },
                      "required": [
                        "id"
                      ],
                      "type": "object"
                    },
                    "permissionLevel": {
                      "enum": [
                        "read",
                        "comment",
                        "edit"
                      ],
                      "type": "string"
                    },
                    "user": {
                      "not": {}
                    }
                  },
                  "required": [
                    "group",
                    "permissionLevel"
                  ],
                  "type": "object"
                }
              ]
            },
            "type": "array"
          }
        },
        "required": [
          "collaborators"
        ],
        "type": "object"
      }
    }
  }
}
