http_endpoint.request_body_spec:
{
  "description": "Authorization request",
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "auth_requirement",
          "user_id"
        ],
        "type": "object",
        "properties": {
          "auth_requirement": {
            "type": "object",
            "properties": {
              "id": {
                "type": "string",
                "description": "one of ID or ProviderID must be set"
              },
              "oauth2": {
                "type": "object",
                "properties": {
                  "scopes": {
                    "type": "array",
                    "items": {
                      "type": "string"
                    }
                  }
                }
              },
              "provider_id": {
                "type": "string",
                "description": "one of ID or ProviderID must be set"
              },
              "provider_type": {
                "type": "string"
              }
            }
          },
          "next_uri": {
            "type": "string",
            "description": "Optional: if provided, the user will be redirected to this URI after authorization"
          },
          "user_id": {
            "type": "string"
          }
        }
      }
    }
  },
  "required": true
}
