http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request to create a role by cloning an existing role.",
        "properties": {
          "data": {
            "description": "Data for the clone role request.",
            "properties": {
              "attributes": {
                "description": "Attributes required to create a new role by cloning an existing one.",
                "properties": {
                  "name": {
                    "description": "Name of the new role that is cloned.",
                    "example": "cloned-role",
                    "type": "string"
                  }
                },
                "required": [
                  "name"
                ],
                "type": "object"
              },
              "type": {
                "default": "roles",
                "description": "Roles type.",
                "enum": [
                  "roles"
                ],
                "example": "roles",
                "type": "string",
                "x-enum-varnames": [
                  "ROLES"
                ]
              }
            },
            "required": [
              "type",
              "attributes"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
