http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "AWS Account Create Request body.",
        "properties": {
          "data": {
            "description": "AWS Account Create Request data.",
            "properties": {
              "attributes": {
                "description": "The AWS Account Integration Config to be created.",
                "properties": {
                  "account_tags": {
                    "description": "Tags to apply to all hosts and metrics reporting for this account. Defaults to `[]`.",
                    "items": {
                      "description": "Tag in the form `key:value`.",
                      "example": "env:prod",
                      "type": "string"
                    },
                    "nullable": true,
                    "type": "array"
                  },
                  "auth_config": {
                    "description": "AWS Authentication config.",
                    "oneOf": [
                      {
                        "description": "AWS Authentication config to integrate your account using an access key pair.",
                        "properties": {
                          "access_key_id": {
                            "description": "AWS Access Key ID.",
                            "example": "AKIAIOSFODNN7EXAMPLE",
                            "type": "string"
                          },
                          "secret_access_key": {
                            "description": "AWS Secret Access Key.",
                            "example": "wJalrXUtnFEMI/K7MDENG/bPxRfiCYEXAMPLEKEY",
                            "minLength": 1,
                            "type": "string",
                            "writeOnly": true
                          }
                        },
                        "required": [
                          "access_key_id"
                        ],
                        "type": "object"
                      },
                      {
                        "description": "AWS Authentication config to integrate your account using an IAM role.",
                        "properties": {
                          "external_id": {
                            "description": "AWS IAM External ID for associated role.",
                            "type": "string"
                          },
                          "role_name": {
                            "description": "AWS IAM Role name.",
                            "example": "DatadogIntegrationRole",
                            "maxLength": 576,
                            "minLength": 1,
                            "type": "string"
                          }
                        },
                        "required": [
                          "role_name"
                        ],
                        "type": "object"
                      }
                    ]
                  },
                  "aws_account_id": {
                    "description": "AWS Account ID.",
                    "example": "123456789012",
                    "type": "string"
                  },
                  "aws_partition": {
                    "description": "AWS partition your AWS account is scoped to. Defaults to `aws`.\nSee [Partitions](https://docs.aws.amazon.com/whitepapers/latest/aws-fault-isolation-boundaries/partitions.html) in the AWS documentation for more information.",
                    "enum": [
                      "aws",
                      "aws-cn",
                      "aws-us-gov"
                    ],
                    "example": "aws",
                    "type": "string",
                    "x-enum-varnames": [
                      "AWS",
                      "AWS_CN",
                      "AWS_US_GOV"
                    ]
                  },
                  "aws_regions": {
                    "description": "AWS Regions to collect data from. Defaults to `include_all`.",
                    "oneOf": [
                      {
                        "description": "Include all regions. Defaults to `true`.",
                        "properties": {
                          "include_all": {
                            "description": "Include all regions.",
                            "example": true,
                            "type": "boolean"
                          }
                        },
                        "required": [
                          "include_all"
                        ],
                        "type": "object"
                      },
                      {
                        "description": "Include only these regions.",
                        "properties": {
                          "include_only": {
                            "description": "Include only these regions.",
                            "example": [
                              "us-east-1"
                            ],
                            "items": {
                              "example": "us-east-1",
                              "type": "string"
                            },
                            "type": "array"
                          }
                        },
                        "required": [
                          "include_only"
                        ],
                        "type": "object"
                      }
                    ]
                  },
                  "logs_config": {
                    "description": "AWS Logs Collection config.",
                    "properties": {
                      "lambda_forwarder": {
                        "description": "Log Autosubscription configuration for Datadog Forwarder Lambda functions. Automatically set up triggers for existing\nand new logs for some services, ensuring no logs from new resources are missed and saving time spent on manual configuration.",
                        "properties": {
                          "lambdas": {
                            "description": "List of Datadog Lambda Log Forwarder ARNs in your AWS account. Defaults to `[]`.",
                            "items": {
                              "example": "arn:aws:lambda:us-east-1:123456789012:function:DatadogLambdaLogForwarder",
                              "type": "string"
                            },
                            "type": "array"
                          },
                          "log_source_config": {
                            "description": "Log source configuration.",
                            "properties": {
                              "tag_filters": {
                                "description": "List of AWS log source tag filters. Defaults to `[]`.",
                                "items": {
                                  "description": "AWS log source tag filter list. Defaults to `[]`.\nArray of log source to AWS resource tag mappings. Each mapping contains a log source and its associated AWS resource tags (in `key:value` format) used to filter logs submitted to Datadog.\nTag filters are applied for tags on the AWS resource emitting logs; tags associated with the log storage entity (such as a CloudWatch Log Group or S3 Bucket) are not considered.\nFor more information on resource tag filter syntax, [see AWS resource exclusion](https://docs.datadoghq.com/account_management/billing/aws/#aws-resource-exclusion) in the AWS integration billing page.",
                                  "properties": {
                                    "source": {
                                      "description": "The AWS log source to which the tag filters defined in `tags` are applied.",
                                      "example": "s3",
                                      "type": "string"
                                    },
                                    "tags": {
                                      "description": "The AWS resource tags to filter on for the log source specified by `source`.",
                                      "items": {
                                        "description": "Tag in the form `key:value`.",
                                        "example": "env:prod",
                                        "type": "string"
                                      },
                                      "nullable": true,
                                      "type": "array"
                                    }
                                  },
                                  "type": "object"
                                },
                                "type": "array"
                              }
                            },
                            "type": "object"
                          },
                          "sources": {
                            "description": "List of service IDs set to enable automatic log collection. Discover the list of available services with the\n[Get list of AWS log ready services](https://docs.datadoghq.com/api/latest/aws-logs-integration/#get-list-of-aws-log-ready-services) endpoint.",
                            "items": {
                              "example": "s3",
                              "type": "string"
                            },
                            "type": "array"
                          }
                        },
                        "type": "object"
                      }
                    },
                    "type": "object"
                  },
                  "metrics_config": {
                    "description": "AWS Metrics Collection config.",
                    "properties": {
                      "automute_enabled": {
                        "description": "Enable EC2 automute for AWS metrics. Defaults to `true`.",
                        "example": true,
                        "type": "boolean"
                      },
                      "collect_cloudwatch_alarms": {
                        "description": "Enable CloudWatch alarms collection. Defaults to `false`.",
                        "example": false,
                        "type": "boolean"
                      },
                      "collect_custom_metrics": {
                        "description": "Enable custom metrics collection. Defaults to `false`.",
                        "example": false,
                        "type": "boolean"
                      },
                      "enabled": {
                        "description": "Enable AWS metrics collection. Defaults to `true`.",
                        "example": true,
                        "type": "boolean"
                      },
                      "namespace_filters": {
                        "description": "AWS Metrics namespace filters. Defaults to `exclude_only`.",
                        "oneOf": [
                          {
                            "description": "Exclude only these namespaces from metrics collection. Defaults to `[\"AWS/SQS\", \"AWS/ElasticMapReduce\", \"AWS/Usage\"]`.\n`AWS/SQS`, `AWS/ElasticMapReduce`, and `AWS/Usage` are excluded by default to reduce your AWS CloudWatch costs from `GetMetricData` API calls.",
                            "properties": {
                              "exclude_only": {
                                "description": "Exclude only these namespaces from metrics collection. Defaults to `[\"AWS/SQS\", \"AWS/ElasticMapReduce\", \"AWS/Usage\"]`.\n`AWS/SQS`, `AWS/ElasticMapReduce`, and `AWS/Usage` are excluded by default to reduce your AWS CloudWatch costs from `GetMetricData` API calls.",
                                "example": [
                                  "AWS/SQS",
                                  "AWS/ElasticMapReduce",
                                  "AWS/Usage"
                                ],
                                "items": {
                                  "example": "AWS/SQS",
                                  "type": "string"
                                },
                                "type": "array"
                              }
                            },
                            "required": [
                              "exclude_only"
                            ],
                            "type": "object"
                          },
                          {
                            "description": "Include only these namespaces.",
                            "properties": {
                              "include_only": {
                                "description": "Include only these namespaces.",
                                "example": [
                                  "AWS/EC2"
                                ],
                                "items": {
                                  "example": "AWS/EC2",
                                  "type": "string"
                                },
                                "type": "array"
                              }
                            },
                            "required": [
                              "include_only"
                            ],
                            "type": "object"
                          }
                        ]
                      },
                      "tag_filters": {
                        "description": "AWS Metrics collection tag filters list. Defaults to `[]`.",
                        "items": {
                          "description": "AWS Metrics Collection tag filters list. Defaults to `[]`.\nThe array of custom AWS resource tags (in the form `key:value`) defines a filter that Datadog uses when collecting metrics from a specified service.\nWildcards, such as `?` (match a single character) and `*` (match multiple characters), and exclusion using `!` before the tag are supported.\nFor EC2, only hosts that match one of the defined tags will be imported into Datadog. The rest will be ignored.\nFor example, `env:production,instance-type:c?.*,!region:us-east-1`.",
                          "properties": {
                            "namespace": {
                              "description": "The AWS service for which the tag filters defined in `tags` will be applied.",
                              "example": "AWS/EC2",
                              "type": "string"
                            },
                            "tags": {
                              "description": "The AWS resource tags to filter on for the service specified by `namespace`.",
                              "items": {
                                "description": "Tag in the form `key:value`.",
                                "example": "datadog:true",
                                "type": "string"
                              },
                              "nullable": true,
                              "type": "array"
                            }
                          },
                          "type": "object"
                        },
                        "type": "array"
                      }
                    },
                    "type": "object"
                  },
                  "resources_config": {
                    "description": "AWS Resources Collection config.",
                    "properties": {
                      "cloud_security_posture_management_collection": {
                        "description": "Enable Cloud Security Management to scan AWS resources for vulnerabilities, misconfigurations, identity risks, and compliance violations. Defaults to `false`. Requires `extended_collection` to be set to `true`.",
                        "example": false,
                        "type": "boolean"
                      },
                      "extended_collection": {
                        "description": "Whether Datadog collects additional attributes and configuration information about the resources in your AWS account. Defaults to `true`. Required for `cloud_security_posture_management_collection`.",
                        "example": true,
                        "type": "boolean"
                      }
                    },
                    "type": "object"
                  },
                  "traces_config": {
                    "description": "AWS Traces Collection config.",
                    "properties": {
                      "xray_services": {
                        "description": "AWS X-Ray services to collect traces from. Defaults to `include_only`.",
                        "oneOf": [
                          {
                            "description": "Include all services.",
                            "properties": {
                              "include_all": {
                                "description": "Include all services.",
                                "example": false,
                                "type": "boolean"
                              }
                            },
                            "required": [
                              "include_all"
                            ],
                            "type": "object"
                          },
                          {
                            "description": "Include only these services. Defaults to `[]`.",
                            "nullable": true,
                            "properties": {
                              "include_only": {
                                "description": "Include only these services.",
                                "example": [
                                  "AWS/AppSync"
                                ],
                                "items": {
                                  "example": "AWS/AppSync",
                                  "type": "string"
                                },
                                "type": "array"
                              }
                            },
                            "required": [
                              "include_only"
                            ],
                            "type": "object"
                          }
                        ]
                      }
                    },
                    "type": "object"
                  }
                },
                "required": [
                  "aws_account_id",
                  "aws_partition",
                  "auth_config"
                ],
                "type": "object"
              },
              "type": {
                "default": "account",
                "description": "AWS Account resource type.",
                "enum": [
                  "account"
                ],
                "example": "account",
                "type": "string",
                "x-enum-varnames": [
                  "ACCOUNT"
                ]
              }
            },
            "required": [
              "attributes",
              "type"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
