http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request to create a team",
        "properties": {
          "data": {
            "description": "Team create",
            "properties": {
              "attributes": {
                "description": "Team creation attributes",
                "properties": {
                  "avatar": {
                    "description": "Unicode representation of the avatar for the team, limited to a single grapheme",
                    "example": "\ud83e\udd51",
                    "nullable": true,
                    "type": "string"
                  },
                  "banner": {
                    "description": "Banner selection for the team",
                    "format": "int64",
                    "nullable": true,
                    "type": "integer"
                  },
                  "description": {
                    "description": "Free-form markdown description/content for the team's homepage",
                    "type": "string"
                  },
                  "handle": {
                    "description": "The team's identifier",
                    "example": "example-team",
                    "maxLength": 195,
                    "type": "string"
                  },
                  "hidden_modules": {
                    "description": "Collection of hidden modules for the team",
                    "items": {
                      "description": "String identifier of the module",
                      "type": "string"
                    },
                    "type": "array"
                  },
                  "name": {
                    "description": "The name of the team",
                    "example": "Example Team",
                    "maxLength": 200,
                    "type": "string"
                  },
                  "visible_modules": {
                    "description": "Collection of visible modules for the team",
                    "items": {
                      "description": "String identifier of the module",
                      "type": "string"
                    },
                    "type": "array"
                  }
                },
                "required": [
                  "handle",
                  "name"
                ],
                "type": "object"
              },
              "relationships": {
                "description": "Relationships formed with the team on creation",
                "properties": {
                  "users": {
                    "description": "Relationship to users.",
                    "properties": {
                      "data": {
                        "description": "Relationships to user objects.",
                        "example": [],
                        "items": {
                          "description": "Relationship to user object.",
                          "properties": {
                            "id": {
                              "description": "A unique identifier that represents the user.",
                              "example": "00000000-0000-0000-2345-000000000000",
                              "type": "string"
                            },
                            "type": {
                              "default": "users",
                              "description": "Users resource type.",
                              "enum": [
                                "users"
                              ],
                              "example": "users",
                              "type": "string",
                              "x-enum-varnames": [
                                "USERS"
                              ]
                            }
                          },
                          "required": [
                            "id",
                            "type"
                          ],
                          "type": "object"
                        },
                        "type": "array"
                      }
                    },
                    "required": [
                      "data"
                    ],
                    "type": "object"
                  }
                },
                "type": "object"
              },
              "type": {
                "default": "team",
                "description": "Team type",
                "enum": [
                  "team"
                ],
                "example": "team",
                "type": "string",
                "x-enum-varnames": [
                  "TEAM"
                ]
              }
            },
            "required": [
              "attributes",
              "type"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
