{
  "content": {
    "application/json": {
      "schema": {
        "description": "The definition of `CreateUploadRequest` object.",
        "properties": {
          "data": {
            "description": "The definition of `CreateUploadRequestData` object.",
            "properties": {
              "attributes": {
                "description": "The definition of `CreateUploadRequestDataAttributes` object.",
                "properties": {
                  "headers": {
                    "description": "The headers of the file to upload.",
                    "example": [
                      ""
                    ],
                    "items": {
                      "type": "string"
                    },
                    "type": "array"
                  },
                  "part_count": {
                    "description": "The number of parts in the upload.",
                    "example": 3,
                    "format": "int32",
                    "maximum": 20,
                    "type": "integer"
                  },
                  "part_size": {
                    "description": "The size of each part in the upload in bytes. For multipart uploads (part_count > 1), all parts except the last one must be at least 5,000,000 bytes. For single-part uploads (part_count = 1), any size is allowed.",
                    "example": 10000000,
                    "format": "int64",
                    "type": "integer"
                  },
                  "table_name": {
                    "description": "The name of the reference table.",
                    "example": "",
                    "type": "string"
                  }
                },
                "required": [
                  "headers",
                  "table_name",
                  "part_count",
                  "part_size"
                ],
                "type": "object"
              },
              "id": {
                "description": "The ID of the upload.",
                "type": "string"
              },
              "type": {
                "default": "upload",
                "description": "Upload resource type.",
                "enum": [
                  "upload"
                ],
                "example": "upload",
                "type": "string",
                "x-enum-varnames": [
                  "UPLOAD"
                ]
              }
            },
            "required": [
              "type"
            ],
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
