http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Create a new rule.",
        "oneOf": [
          {
            "description": "Create a new rule.",
            "properties": {
              "calculatedFields": {
                "description": "Calculated fields. Only allowed for scheduled rules - in other words, when schedulingOptions is also defined.",
                "items": {
                  "description": "Calculated field.",
                  "properties": {
                    "expression": {
                      "description": "Expression.",
                      "example": "@request_end_timestamp - @request_start_timestamp",
                      "type": "string"
                    },
                    "name": {
                      "description": "Field name.",
                      "example": "response_time",
                      "type": "string"
                    }
                  },
                  "required": [
                    "name",
                    "expression"
                  ],
                  "type": "object"
                },
                "type": "array"
              },
              "cases": {
                "description": "Cases for generating signals.",
                "example": [],
                "items": {
                  "description": "Case when signal is generated.",
                  "properties": {
                    "actions": {
                      "description": "Action to perform for each rule case.",
                      "items": {
                        "description": "Action to perform when a signal is triggered. Only available for Application Security rule type.",
                        "properties": {
                          "options": {
                            "additionalProperties": {},
                            "description": "Options for the rule action",
                            "properties": {
                              "duration": {
                                "description": "Duration of the action in seconds. 0 indicates no expiration.",
                                "example": 0,
                                "format": "int64",
                                "minimum": 0,
                                "type": "integer"
                              },
                              "flaggedIPType": {
                                "description": "Used with the case action of type 'flag_ip'. The value specified in this field is applied as a flag to the IP addresses.",
                                "enum": [
                                  "SUSPICIOUS",
                                  "FLAGGED"
                                ],
                                "example": "FLAGGED",
                                "type": "string",
                                "x-enum-varnames": [
                                  "SUSPICIOUS",
                                  "FLAGGED"
                                ]
                              },
                              "userBehaviorName": {
                                "description": "Used with the case action of type 'user_behavior'. The value specified in this field is applied as a risk tag to all users affected by the rule.",
                                "type": "string"
                              }
                            },
                            "type": "object"
                          },
                          "type": {
                            "description": "The action type.",
                            "enum": [
                              "block_ip",
                              "block_user",
                              "user_behavior",
                              "flag_ip"
                            ],
                            "type": "string",
                            "x-enum-varnames": [
                              "BLOCK_IP",
                              "BLOCK_USER",
                              "USER_BEHAVIOR",
                              "FLAG_IP"
                            ]
                          }
                        },
                        "type": "object"
                      },
                      "type": "array"
                    },
                    "condition": {
                      "description": "A case contains logical operations (`>`,`>=`, `&&`, `||`) to determine if a signal should be generated\nbased on the event counts in the previously defined queries.",
                      "type": "string"
                    },
                    "name": {
                      "description": "Name of the case.",
                      "type": "string"
                    },
                    "notifications": {
                      "description": "Notification targets.",
                      "items": {
                        "description": "Notification.",
                        "type": "string"
                      },
                      "type": "array"
                    },
                    "status": {
                      "description": "Severity of the Security Signal.",
                      "enum": [
                        "info",
                        "low",
                        "medium",
                        "high",
                        "critical"
                      ],
                      "example": "critical",
                      "type": "string",
                      "x-enum-varnames": [
                        "INFO",
                        "LOW",
                        "MEDIUM",
                        "HIGH",
                        "CRITICAL"
                      ]
                    }
                  },
                  "required": [
                    "status"
                  ],
                  "type": "object"
                },
                "type": "array"
              },
              "filters": {
                "description": "Additional queries to filter matched events before they are processed. This field is deprecated for log detection, signal correlation, and workload security rules.",
                "items": {
                  "description": "The rule's suppression filter.",
                  "properties": {
                    "action": {
                      "description": "The type of filtering action.",
                      "enum": [
                        "require",
                        "suppress"
                      ],
                      "type": "string",
                      "x-enum-varnames": [
                        "REQUIRE",
                        "SUPPRESS"
                      ]
                    },
                    "query": {
                      "description": "Query for selecting logs to apply the filtering action.",
                      "type": "string"
                    }
                  },
                  "type": "object"
                },
                "type": "array"
              },
              "groupSignalsBy": {
                "description": "Additional grouping to perform on top of the existing groups in the query section. Must be a subset of the existing groups.",
                "example": [
                  "service"
                ],
                "items": {
                  "description": "Field to group by.",
                  "type": "string"
                },
                "type": "array"
              },
              "hasExtendedTitle": {
                "description": "Whether the notifications include the triggering group-by values in their title.",
                "example": true,
                "type": "boolean"
              },
              "isEnabled": {
                "description": "Whether the rule is enabled.",
                "example": true,
                "type": "boolean"
              },
              "message": {
                "description": "Message for generated signals.",
                "example": "",
                "type": "string"
              },
              "name": {
                "description": "The name of the rule.",
                "example": "My security monitoring rule.",
                "type": "string"
              },
              "options": {
                "description": "Options.",
                "properties": {
                  "complianceRuleOptions": {
                    "additionalProperties": {},
                    "description": "Options for cloud_configuration rules.\nFields `resourceType` and `regoRule` are mandatory when managing custom `cloud_configuration` rules.",
                    "properties": {
                      "complexRule": {
                        "description": "Whether the rule is a complex one.\nMust be set to true if `regoRule.resourceTypes` contains more than one item. Defaults to false.",
                        "type": "boolean"
                      },
                      "regoRule": {
                        "description": "Rule details.",
                        "properties": {
                          "policy": {
                            "description": "The policy written in `rego`, see: https://www.openpolicyagent.org/docs/latest/policy-language/",
                            "example": "package datadog\n\nimport data.datadog.output as dd_output\nimport future.keywords.contains\nimport future.keywords.if\nimport future.keywords.in\n\neval(resource) = \"skip\" if {\n  # Logic that evaluates to true if the resource should be skipped\n  true\n} else = \"pass\" {\n  # Logic that evaluates to true if the resource is compliant\n  true\n} else = \"fail\" {\n  # Logic that evaluates to true if the resource is not compliant\n  true\n}\n\n# This part remains unchanged for all rules\nresults contains result if {\n  some resource in input.resources[input.main_resource_type]\n  result := dd_output.format(resource, eval(resource))\n}",
                            "type": "string"
                          },
                          "resourceTypes": {
                            "description": "List of resource types that will be evaluated upon. Must have at least one element.",
                            "example": [
                              "gcp_iam_service_account",
                              "gcp_iam_policy"
                            ],
                            "items": {
                              "type": "string"
                            },
                            "type": "array"
                          }
                        },
                        "required": [
                          "policy",
                          "resourceTypes"
                        ],
                        "type": "object"
                      },
                      "resourceType": {
                        "description": "Main resource type to be checked by the rule. It should be specified again in `regoRule.resourceTypes`.",
                        "example": "aws_acm",
                        "type": "string"
                      }
                    },
                    "type": "object"
                  },
                  "decreaseCriticalityBasedOnEnv": {
                    "description": "If true, signals in non-production environments have a lower severity than what is defined by the rule case, which can reduce signal noise.\nThe severity is decreased by one level: `CRITICAL` in production becomes `HIGH` in non-production, `HIGH` becomes `MEDIUM` and so on. `INFO` remains `INFO`.\nThe decrement is applied when the environment tag of the signal starts with `staging`, `test` or `dev`.",
                    "example": false,
                    "type": "boolean"
                  },
                  "detectionMethod": {
                    "description": "The detection method.",
                    "enum": [
                      "threshold",
                      "new_value",
                      "anomaly_detection",
                      "impossible_travel",
                      "hardcoded",
                      "third_party",
                      "anomaly_threshold",
                      "sequence_detection"
                    ],
                    "type": "string",
                    "x-enum-varnames": [
                      "THRESHOLD",
                      "NEW_VALUE",
                      "ANOMALY_DETECTION",
                      "IMPOSSIBLE_TRAVEL",
                      "HARDCODED",
                      "THIRD_PARTY",
                      "ANOMALY_THRESHOLD",
                      "SEQUENCE_DETECTION"
                    ]
                  },
                  "evaluationWindow": {
                    "description": "A time window is specified to match when at least one of the cases matches true. This is a sliding window\nand evaluates in real time. For third party detection method, this field is not used.",
                    "enum": [
                      0,
                      60,
                      300,
                      600,
                      900,
                      1800,
                      3600,
                      7200,
                      10800,
                      21600,
                      43200,
                      86400
                    ],
                    "format": "int32",
                    "type": "integer",
                    "x-enum-varnames": [
                      "ZERO_MINUTES",
                      "ONE_MINUTE",
                      "FIVE_MINUTES",
                      "TEN_MINUTES",
                      "FIFTEEN_MINUTES",
                      "THIRTY_MINUTES",
                      "ONE_HOUR",
                      "TWO_HOURS",
                      "THREE_HOURS",
                      "SIX_HOURS",
                      "TWELVE_HOURS",
                      "ONE_DAY"
                    ]
                  },
                  "hardcodedEvaluatorType": {
                    "description": "Hardcoded evaluator type.",
                    "enum": [
                      "log4shell"
                    ],
                    "type": "string",
                    "x-enum-varnames": [
                      "LOG4SHELL"
                    ]
                  },
                  "impossibleTravelOptions": {
                    "description": "Options on impossible travel detection method.",
                    "properties": {
                      "baselineUserLocations": {
                        "description": "If true, signals are suppressed for the first 24 hours. In that time, Datadog learns the user's regular\naccess locations. This can be helpful to reduce noise and infer VPN usage or credentialed API access.",
                        "example": true,
                        "type": "boolean"
                      }
                    },
                    "type": "object"
                  },
                  "keepAlive": {
                    "description": "Once a signal is generated, the signal will remain \"open\" if a case is matched at least once within\nthis keep alive window. For third party detection method, this field is not used.",
                    "enum": [
                      0,
                      60,
                      300,
                      600,
                      900,
                      1800,
                      3600,
                      7200,
                      10800,
                      21600,
                      43200,
                      86400
                    ],
                    "format": "int32",
                    "type": "integer",
                    "x-enum-varnames": [
                      "ZERO_MINUTES",
                      "ONE_MINUTE",
                      "FIVE_MINUTES",
                      "TEN_MINUTES",
                      "FIFTEEN_MINUTES",
                      "THIRTY_MINUTES",
                      "ONE_HOUR",
                      "TWO_HOURS",
                      "THREE_HOURS",
                      "SIX_HOURS",
                      "TWELVE_HOURS",
                      "ONE_DAY"
                    ]
                  },
                  "maxSignalDuration": {
                    "description": "A signal will \"close\" regardless of the query being matched once the time exceeds the maximum duration.\nThis time is calculated from the first seen timestamp.",
                    "enum": [
                      0,
                      60,
                      300,
                      600,
                      900,
                      1800,
                      3600,
                      7200,
                      10800,
                      21600,
                      43200,
                      86400
                    ],
                    "format": "int32",
                    "type": "integer",
                    "x-enum-varnames": [
                      "ZERO_MINUTES",
                      "ONE_MINUTE",
                      "FIVE_MINUTES",
                      "TEN_MINUTES",
                      "FIFTEEN_MINUTES",
                      "THIRTY_MINUTES",
                      "ONE_HOUR",
                      "TWO_HOURS",
                      "THREE_HOURS",
                      "SIX_HOURS",
                      "TWELVE_HOURS",
                      "ONE_DAY"
                    ]
                  },
                  "newValueOptions": {
                    "description": "Options on new value detection method.",
                    "properties": {
                      "forgetAfter": {
                        "description": "The duration in days after which a learned value is forgotten.",
                        "enum": [
                          1,
                          2,
                          7,
                          14,
                          21,
                          28
                        ],
                        "format": "int32",
                        "type": "integer",
                        "x-enum-varnames": [
                          "ONE_DAY",
                          "TWO_DAYS",
                          "ONE_WEEK",
                          "TWO_WEEKS",
                          "THREE_WEEKS",
                          "FOUR_WEEKS"
                        ]
                      },
                      "learningDuration": {
                        "default": 0,
                        "description": "The duration in days during which values are learned, and after which signals will be generated for values that\nweren't learned. If set to 0, a signal will be generated for all new values after the first value is learned.",
                        "enum": [
                          0,
                          1,
                          7
                        ],
                        "format": "int32",
                        "type": "integer",
                        "x-enum-varnames": [
                          "ZERO_DAYS",
                          "ONE_DAY",
                          "SEVEN_DAYS"
                        ]
                      },
                      "learningMethod": {
                        "default": "duration",
                        "description": "The learning method used to determine when signals should be generated for values that weren't learned.",
                        "enum": [
                          "duration",
                          "threshold"
                        ],
                        "type": "string",
                        "x-enum-varnames": [
                          "DURATION",
                          "THRESHOLD"
                        ]
                      },
                      "learningThreshold": {
                        "default": 0,
                        "description": "A number of occurrences after which signals will be generated for values that weren't learned.",
                        "enum": [
                          0,
                          1
                        ],
                        "format": "int32",
                        "type": "integer",
                        "x-enum-varnames": [
                          "ZERO_OCCURRENCES",
                          "ONE_OCCURRENCE"
                        ]
                      }
                    },
                    "type": "object"
                  },
                  "sequenceDetectionOptions": {
                    "description": "Options on sequence detection method.",
                    "properties": {
                      "stepTransitions": {
                        "description": "Transitions defining the allowed order of steps and their evaluation windows.",
                        "items": {
                          "description": "Transition from a parent step to a child step within a sequence detection rule.",
                          "properties": {
                            "child": {
                              "description": "Name of the child step.",
                              "type": "string"
                            },
                            "evaluationWindow": {
                              "description": "A time window is specified to match when at least one of the cases matches true. This is a sliding window\nand evaluates in real time. For third party detection method, this field is not used.",
                              "enum": [
                                0,
                                60,
                                300,
                                600,
                                900,
                                1800,
                                3600,
                                7200,
                                10800,
                                21600,
                                43200,
                                86400
                              ],
                              "format": "int32",
                              "type": "integer",
                              "x-enum-varnames": [
                                "ZERO_MINUTES",
                                "ONE_MINUTE",
                                "FIVE_MINUTES",
                                "TEN_MINUTES",
                                "FIFTEEN_MINUTES",
                                "THIRTY_MINUTES",
                                "ONE_HOUR",
                                "TWO_HOURS",
                                "THREE_HOURS",
                                "SIX_HOURS",
                                "TWELVE_HOURS",
                                "ONE_DAY"
                              ]
                            },
                            "parent": {
                              "description": "Name of the parent step.",
                              "type": "string"
                            }
                          },
                          "type": "object"
                        },
                        "type": "array"
                      },
                      "steps": {
                        "description": "Steps that define the conditions to be matched in sequence.",
                        "items": {
                          "description": "Step definition for sequence detection containing the step name, condition, and evaluation window.",
                          "properties": {
                            "condition": {
                              "description": "Condition referencing rule queries (e.g., `a > 0`).",
                              "type": "string"
                            },
                            "evaluationWindow": {
                              "description": "A time window is specified to match when at least one of the cases matches true. This is a sliding window\nand evaluates in real time. For third party detection method, this field is not used.",
                              "enum": [
                                0,
                                60,
                                300,
                                600,
                                900,
                                1800,
                                3600,
                                7200,
                                10800,
                                21600,
                                43200,
                                86400
                              ],
                              "format": "int32",
                              "type": "integer",
                              "x-enum-varnames": [
                                "ZERO_MINUTES",
                                "ONE_MINUTE",
                                "FIVE_MINUTES",
                                "TEN_MINUTES",
                                "FIFTEEN_MINUTES",
                                "THIRTY_MINUTES",
                                "ONE_HOUR",
                                "TWO_HOURS",
                                "THREE_HOURS",
                                "SIX_HOURS",
                                "TWELVE_HOURS",
                                "ONE_DAY"
                              ]
                            },
                            "name": {
                              "description": "Unique name identifying the step.",
                              "type": "string"
                            }
                          },
                          "type": "object"
                        },
                        "type": "array"
                      }
                    },
                    "type": "object"
                  },
                  "thirdPartyRuleOptions": {
                    "description": "Options on third party detection method.",
                    "properties": {
                      "defaultNotifications": {
                        "description": "Notification targets for the logs that do not correspond to any of the cases.",
                        "items": {
                          "description": "Notification.",
                          "type": "string"
                        },
                        "type": "array"
                      },
                      "defaultStatus": {
                        "description": "Severity of the Security Signal.",
                        "enum": [
                          "info",
                          "low",
                          "medium",
                          "high",
                          "critical"
                        ],
                        "example": "critical",
                        "type": "string",
                        "x-enum-varnames": [
                          "INFO",
                          "LOW",
                          "MEDIUM",
                          "HIGH",
                          "CRITICAL"
                        ]
                      },
                      "rootQueries": {
                        "description": "Queries to be combined with third party case queries. Each of them can have different group by fields, to aggregate differently based on the type of alert.",
                        "items": {
                          "description": "A query to be combined with the third party case query.",
                          "properties": {
                            "groupByFields": {
                              "description": "Fields to group by.",
                              "items": {
                                "description": "Field.",
                                "type": "string"
                              },
                              "type": "array"
                            },
                            "query": {
                              "description": "Query to run on logs.",
                              "example": "source:cloudtrail",
                              "type": "string"
                            }
                          },
                          "type": "object"
                        },
                        "type": "array"
                      },
                      "signalTitleTemplate": {
                        "description": "A template for the signal title; if omitted, the title is generated based on the case name.",
                        "type": "string"
                      }
                    },
                    "type": "object"
                  }
                },
                "type": "object"
              },
              "queries": {
                "description": "Queries for selecting logs which are part of the rule.",
                "example": [],
                "items": {
                  "description": "Query for matching rule.",
                  "properties": {
                    "aggregation": {
                      "description": "The aggregation type.",
                      "enum": [
                        "count",
                        "cardinality",
                        "sum",
                        "max",
                        "new_value",
                        "geo_data",
                        "event_count",
                        "none"
                      ],
                      "type": "string",
                      "x-enum-varnames": [
                        "COUNT",
                        "CARDINALITY",
                        "SUM",
                        "MAX",
                        "NEW_VALUE",
                        "GEO_DATA",
                        "EVENT_COUNT",
                        "NONE"
                      ]
                    },
                    "customQueryExtension": {
                      "description": "Query extension to append to the logs query.",
                      "example": "a > 3",
                      "type": "string"
                    },
                    "dataSource": {
                      "default": "logs",
                      "description": "Source of events, either logs, audit trail, or Datadog events.",
                      "enum": [
                        "logs",
                        "audit",
                        "app_sec_spans",
                        "spans",
                        "security_runtime",
                        "network",
                        "events"
                      ],
                      "example": "logs",
                      "type": "string",
                      "x-enum-varnames": [
                        "LOGS",
                        "AUDIT",
                        "APP_SEC_SPANS",
                        "SPANS",
                        "SECURITY_RUNTIME",
                        "NETWORK",
                        "EVENTS"
                      ]
                    },
                    "distinctFields": {
                      "description": "Field for which the cardinality is measured. Sent as an array.",
                      "items": {
                        "description": "Field.",
                        "type": "string"
                      },
                      "type": "array"
                    },
                    "groupByFields": {
                      "description": "Fields to group by.",
                      "items": {
                        "description": "Field.",
                        "type": "string"
                      },
                      "type": "array"
                    },
                    "hasOptionalGroupByFields": {
                      "default": false,
                      "description": "When false, events without a group-by value are ignored by the rule. When true, events with missing group-by fields are processed with `N/A`, replacing the missing values.",
                      "example": false,
                      "type": "boolean"
                    },
                    "index": {
                      "description": "**This field is currently unstable and might be removed in a minor version upgrade.**\nThe index to run the query on, if the `dataSource` is `logs`. Only used for scheduled rules - in other words, when the `schedulingOptions` field is present in the rule payload.",
                      "type": "string"
                    },
                    "indexes": {
                      "description": "List of indexes to query when the `dataSource` is `logs`. Only used for scheduled rules, such as when the `schedulingOptions` field is present in the rule payload.",
                      "items": {
                        "description": "Index.",
                        "type": "string"
                      },
                      "type": "array"
                    },
                    "metric": {
                      "deprecated": true,
                      "description": "(Deprecated) The target field to aggregate over when using the sum or max\naggregations. `metrics` field should be used instead.",
                      "type": "string"
                    },
                    "metrics": {
                      "description": "Group of target fields to aggregate over when using the sum, max, geo data, or new value aggregations. The sum, max, and geo data aggregations only accept one value in this list, whereas the new value aggregation accepts up to five values.",
                      "items": {
                        "description": "Field.",
                        "type": "string"
                      },
                      "type": "array"
                    },
                    "name": {
                      "description": "Name of the query.",
                      "type": "string"
                    },
                    "query": {
                      "description": "Query to run on logs.",
                      "example": "a > 3",
                      "type": "string"
                    }
                  },
                  "type": "object"
                },
                "type": "array"
              },
              "referenceTables": {
                "description": "Reference tables for the rule.",
                "items": {
                  "description": "Reference tables used in the queries.",
                  "properties": {
                    "checkPresence": {
                      "description": "Whether to include or exclude the matched values.",
                      "type": "boolean"
                    },
                    "columnName": {
                      "description": "The name of the column in the reference table.",
                      "type": "string"
                    },
                    "logFieldPath": {
                      "description": "The field in the log to match against the reference table.",
                      "type": "string"
                    },
                    "ruleQueryName": {
                      "description": "The name of the query to apply the reference table to.",
                      "type": "string"
                    },
                    "tableName": {
                      "description": "The name of the reference table.",
                      "type": "string"
                    }
                  },
                  "type": "object"
                },
                "type": "array"
              },
              "schedulingOptions": {
                "description": "Options for scheduled rules. When this field is present, the rule runs based on the schedule. When absent, it runs real-time on ingested logs.",
                "nullable": true,
                "properties": {
                  "rrule": {
                    "description": "Schedule for the rule queries, written in RRULE syntax. See [RFC](https://icalendar.org/iCalendar-RFC-5545/3-8-5-3-recurrence-rule.html) for syntax reference.",
                    "example": "FREQ=HOURLY;INTERVAL=1;",
                    "type": "string"
                  },
                  "start": {
                    "description": "Start date for the schedule, in ISO 8601 format without timezone.",
                    "example": "2025-07-14T12:00:00",
                    "type": "string"
                  },
                  "timezone": {
                    "description": "Time zone of the start date, in the [tz database](https://en.wikipedia.org/wiki/List_of_tz_database_time_zones) format.",
                    "example": "America/New_York",
                    "type": "string"
                  }
                },
                "type": "object"
              },
              "tags": {
                "description": "Tags for generated signals.",
                "example": [
                  "env:prod",
                  "team:security"
                ],
                "items": {
                  "description": "Tag.",
                  "type": "string"
                },
                "type": "array"
              },
              "thirdPartyCases": {
                "description": "Cases for generating signals from third-party rules. Only available for third-party rules.",
                "example": [],
                "items": {
                  "description": "Case when a signal is generated by a third party rule.",
                  "properties": {
                    "name": {
                      "description": "Name of the case.",
                      "type": "string"
                    },
                    "notifications": {
                      "description": "Notification targets for each case.",
                      "items": {
                        "description": "Notification.",
                        "type": "string"
                      },
                      "type": "array"
                    },
                    "query": {
