http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Team sync request.",
        "example": {
          "data": {
            "attributes": {
              "source": "github",
              "type": "link"
            },
            "type": "team_sync_bulk"
          }
        },
        "properties": {
          "data": {
            "description": "Team sync data.",
            "properties": {
              "attributes": {
                "description": "Team sync attributes.",
                "properties": {
                  "source": {
                    "description": "The external source platform for team synchronization. Only \"github\" is supported.",
                    "enum": [
                      "github"
                    ],
                    "example": "github",
                    "type": "string",
                    "x-enum-varnames": [
                      "GITHUB"
                    ]
                  },
                  "type": {
                    "description": "The type of synchronization operation. Only \"link\" is supported, which links existing teams by matching names.",
                    "enum": [
                      "link"
                    ],
                    "example": "link",
                    "type": "string",
                    "x-enum-varnames": [
                      "LINK"
                    ]
                  }
                },
                "required": [
                  "source",
                  "type"
                ],
                "type": "object"
              },
              "type": {
                "description": "Team sync bulk type.",
                "enum": [
                  "team_sync_bulk"
                ],
                "example": "team_sync_bulk",
                "type": "string",
                "x-enum-varnames": [
                  "TEAM_SYNC_BULK"
                ]
              }
            },
            "required": [
              "attributes",
              "type"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
