http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "The logs archive.",
        "properties": {
          "data": {
            "description": "The definition of an archive.",
            "properties": {
              "attributes": {
                "description": "The attributes associated with the archive.",
                "properties": {
                  "destination": {
                    "description": "An archive's destination.",
                    "oneOf": [
                      {
                        "description": "The Azure archive destination.",
                        "properties": {
                          "container": {
                            "description": "The container where the archive will be stored.",
                            "example": "container-name",
                            "type": "string"
                          },
                          "integration": {
                            "description": "The Azure archive's integration destination.",
                            "properties": {
                              "client_id": {
                                "description": "A client ID.",
                                "example": "aaaaaaaa-1a1a-1a1a-1a1a-aaaaaaaaaaaa",
                                "type": "string"
                              },
                              "tenant_id": {
                                "description": "A tenant ID.",
                                "example": "aaaaaaaa-1a1a-1a1a-1a1a-aaaaaaaaaaaa",
                                "type": "string"
                              }
                            },
                            "required": [
                              "tenant_id",
                              "client_id"
                            ],
                            "type": "object"
                          },
                          "path": {
                            "description": "The archive path.",
                            "type": "string"
                          },
                          "region": {
                            "description": "The region where the archive will be stored.",
                            "type": "string"
                          },
                          "storage_account": {
                            "description": "The associated storage account.",
                            "example": "account-name",
                            "type": "string"
                          },
                          "type": {
                            "default": "azure",
                            "description": "Type of the Azure archive destination.",
                            "enum": [
                              "azure"
                            ],
                            "example": "azure",
                            "type": "string",
                            "x-enum-varnames": [
                              "AZURE"
                            ]
                          }
                        },
                        "required": [
                          "storage_account",
                          "container",
                          "integration",
                          "type"
                        ],
                        "type": "object"
                      },
                      {
                        "description": "The GCS archive destination.",
                        "properties": {
                          "bucket": {
                            "description": "The bucket where the archive will be stored.",
                            "example": "bucket-name",
                            "type": "string"
                          },
                          "integration": {
                            "description": "The GCS archive's integration destination.",
                            "properties": {
                              "client_email": {
                                "description": "A client email.",
                                "example": "youremail@example.com",
                                "type": "string"
                              },
                              "project_id": {
                                "description": "A project ID.",
                                "example": "project-id",
                                "type": "string"
                              }
                            },
                            "required": [
                              "client_email"
                            ],
                            "type": "object"
                          },
                          "path": {
                            "description": "The archive path.",
                            "type": "string"
                          },
                          "type": {
                            "default": "gcs",
                            "description": "Type of the GCS archive destination.",
                            "enum": [
                              "gcs"
                            ],
                            "example": "gcs",
                            "type": "string",
                            "x-enum-varnames": [
                              "GCS"
                            ]
                          }
                        },
                        "required": [
                          "bucket",
                          "integration",
                          "type"
                        ],
                        "type": "object"
                      },
                      {
                        "description": "The S3 archive destination.",
                        "properties": {
                          "bucket": {
                            "description": "The bucket where the archive will be stored.",
                            "example": "bucket-name",
                            "type": "string"
                          },
                          "encryption": {
                            "description": "The S3 encryption settings.",
                            "properties": {
                              "key": {
                                "description": "An Amazon Resource Name (ARN) used to identify an AWS KMS key.",
                                "example": "arn:aws:kms:us-east-1:012345678901:key/DatadogIntegrationRoleKms",
                                "type": "string"
                              },
                              "type": {
                                "description": "Type of S3 encryption for a destination.",
                                "enum": [
                                  "NO_OVERRIDE",
                                  "SSE_S3",
                                  "SSE_KMS"
                                ],
                                "example": "SSE_S3",
                                "type": "string",
                                "x-enum-varnames": [
                                  "NO_OVERRIDE",
                                  "SSE_S3",
                                  "SSE_KMS"
                                ]
                              }
                            },
                            "required": [
                              "type"
                            ],
                            "type": "object"
                          },
                          "integration": {
                            "description": "The S3 Archive's integration destination.",
                            "properties": {
                              "account_id": {
                                "description": "The account ID for the integration.",
                                "example": "123456789012",
                                "type": "string"
                              },
                              "role_name": {
                                "description": "The path of the integration.",
                                "example": "role-name",
                                "type": "string"
                              }
                            },
                            "required": [
                              "role_name",
                              "account_id"
                            ],
                            "type": "object"
                          },
                          "path": {
                            "description": "The archive path.",
                            "type": "string"
                          },
                          "storage_class": {
                            "default": "STANDARD",
                            "description": "The storage class where the archive will be stored.",
                            "enum": [
                              "STANDARD",
                              "STANDARD_IA",
                              "ONEZONE_IA",
                              "INTELLIGENT_TIERING",
                              "GLACIER_IR"
                            ],
                            "example": "STANDARD",
                            "type": "string",
                            "x-enum-varnames": [
                              "STANDARD",
                              "STANDARD_IA",
                              "ONEZONE_IA",
                              "INTELLIGENT_TIERING",
                              "GLACIER_IR"
                            ]
                          },
                          "type": {
                            "default": "s3",
                            "description": "Type of the S3 archive destination.",
                            "enum": [
                              "s3"
                            ],
                            "example": "s3",
                            "type": "string",
                            "x-enum-varnames": [
                              "S3"
                            ]
                          }
                        },
                        "required": [
                          "bucket",
                          "integration",
                          "type"
                        ],
                        "type": "object"
                      }
                    ]
                  },
                  "include_tags": {
                    "default": false,
                    "description": "To store the tags in the archive, set the value \"true\".\nIf it is set to \"false\", the tags will be deleted when the logs are sent to the archive.",
                    "example": false,
                    "type": "boolean"
                  },
                  "name": {
                    "description": "The archive name.",
                    "example": "Nginx Archive",
                    "type": "string"
                  },
                  "query": {
                    "description": "The archive query/filter. Logs matching this query are included in the archive.",
                    "example": "source:nginx",
                    "type": "string"
                  },
                  "rehydration_max_scan_size_in_gb": {
                    "description": "Maximum scan size for rehydration from this archive.",
                    "example": 100,
                    "format": "int64",
                    "nullable": true,
                    "type": "integer"
                  },
                  "rehydration_tags": {
                    "description": "An array of tags to add to rehydrated logs from an archive.",
                    "example": [
                      "team:intake",
                      "team:app"
                    ],
                    "items": {
                      "description": "A given tag in the `<KEY>:<VALUE>` format.",
                      "type": "string"
                    },
                    "type": "array"
                  }
                },
                "required": [
                  "name",
                  "query",
                  "destination"
                ],
                "type": "object"
              },
              "type": {
                "default": "archives",
                "description": "The type of the resource. The value should always be archives.",
                "example": "archives",
                "type": "string"
              }
            },
            "required": [
              "type"
            ],
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "description": "New definition of the archive.",
  "required": true
}
