http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request containing the list of dashboards to update to.",
        "properties": {
          "dashboards": {
            "description": "List of dashboards to update the dashboard list to.",
            "items": {
              "description": "A dashboard within a list.",
              "properties": {
                "id": {
                  "description": "ID of the dashboard.",
                  "example": "q5j-nti-fv6",
                  "type": "string"
                },
                "type": {
                  "description": "The type of the dashboard.",
                  "enum": [
                    "custom_timeboard",
                    "custom_screenboard",
                    "integration_screenboard",
                    "integration_timeboard",
                    "host_timeboard"
                  ],
                  "example": "host_timeboard",
                  "type": "string",
                  "x-enum-varnames": [
                    "CUSTOM_TIMEBOARD",
                    "CUSTOM_SCREENBOARD",
                    "INTEGRATION_SCREENBOARD",
                    "INTEGRATION_TIMEBOARD",
                    "HOST_TIMEBOARD"
                  ]
                }
              },
              "required": [
                "type",
                "id"
              ],
              "type": "object"
            },
            "type": "array"
          }
        },
        "type": "object"
      }
    }
  },
  "description": "New dashboards of the dashboard list.",
  "required": true
}
