http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Request to update specific fields on an existing datastore item.",
        "properties": {
          "data": {
            "description": "Data wrapper containing the item identifier and the changes to apply during the update operation.",
            "properties": {
              "attributes": {
                "description": "Attributes for updating a datastore item, including the item key and changes to apply.",
                "properties": {
                  "id": {
                    "description": "The unique identifier of the item being updated.",
                    "type": "string"
                  },
                  "item_changes": {
                    "description": "Changes to apply to a datastore item using set operations.",
                    "properties": {
                      "ops_set": {
                        "additionalProperties": {},
                        "description": "Set operation that contains key-value pairs to set on the datastore item.",
                        "type": "object"
                      }
                    },
                    "type": "object"
                  },
                  "item_key": {
                    "description": "The primary key that identifies the item to update. Cannot exceed 256 characters.",
                    "example": "",
                    "maxLength": 256,
                    "type": "string"
                  }
                },
                "required": [
                  "item_changes",
                  "item_key"
                ],
                "type": "object"
              },
              "id": {
                "description": "The unique identifier of the datastore item.",
                "type": "string"
              },
              "type": {
                "default": "items",
                "description": "The resource type for datastore items.",
                "enum": [
                  "items"
                ],
                "example": "items",
                "type": "string",
                "x-enum-varnames": [
                  "ITEMS"
                ]
              }
            },
            "required": [
              "type"
            ],
            "type": "object"
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
