http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Create or update kind request.",
        "oneOf": [
          {
            "description": "Schema for kind.",
            "properties": {
              "description": {
                "description": "Short description of the kind.",
                "type": "string"
              },
              "displayName": {
                "description": "The display name of the kind. Automatically generated if not provided.",
                "type": "string"
              },
              "kind": {
                "description": "The name of the kind to create or update. This must be in kebab-case format.",
                "example": "my-job",
                "type": "string"
              }
            },
            "required": [
              "kind"
            ],
            "type": "object"
          },
          {
            "description": "Kind definition in raw JSON or YAML representation.",
            "example": "kind: service\ndisplayName: Service\ndescription: A service entity in the catalog.",
            "type": "string"
          }
        ]
      }
    }
  },
  "description": "Kind YAML or JSON.",
  "required": true
}
