http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "description": "Team update request",
        "properties": {
          "data": {
            "description": "Team update request",
            "properties": {
              "attributes": {
                "description": "Team update attributes",
                "properties": {
                  "avatar": {
                    "description": "Unicode representation of the avatar for the team, limited to a single grapheme",
                    "example": "\ud83e\udd51",
                    "nullable": true,
                    "type": "string"
                  },
                  "banner": {
                    "description": "Banner selection for the team",
                    "format": "int64",
                    "nullable": true,
                    "type": "integer"
                  },
                  "description": {
                    "description": "Free-form markdown description/content for the team's homepage",
                    "type": "string"
                  },
                  "handle": {
                    "description": "The team's identifier",
                    "example": "example-team",
                    "maxLength": 195,
                    "type": "string"
                  },
                  "hidden_modules": {
                    "description": "Collection of hidden modules for the team",
                    "items": {
                      "description": "String identifier of the module",
                      "type": "string"
                    },
                    "type": "array"
                  },
                  "name": {
                    "description": "The name of the team",
                    "example": "Example Team",
                    "maxLength": 200,
                    "type": "string"
                  },
                  "visible_modules": {
                    "description": "Collection of visible modules for the team",
                    "items": {
                      "description": "String identifier of the module",
                      "type": "string"
                    },
                    "type": "array"
                  }
                },
                "required": [
                  "handle",
                  "name"
                ],
                "type": "object"
              },
              "relationships": {
                "description": "Team update relationships",
                "properties": {
                  "team_links": {
                    "description": "Relationship between a team and a team link",
                    "properties": {
                      "data": {
                        "description": "Related team links",
                        "items": {
                          "description": "Relationship between a link and a team",
                          "properties": {
                            "id": {
                              "description": "The team link's identifier",
                              "example": "f9bb8444-af7f-11ec-ac2c-da7ad0900001",
                              "type": "string"
                            },
                            "type": {
                              "default": "team_links",
                              "description": "Team link type",
                              "enum": [
                                "team_links"
                              ],
                              "example": "team_links",
                              "type": "string",
                              "x-enum-varnames": [
                                "TEAM_LINKS"
                              ]
                            }
                          },
                          "required": [
                            "id",
                            "type"
                          ],
                          "type": "object"
                        },
                        "type": "array"
                      },
                      "links": {
                        "description": "Links attributes.",
                        "properties": {
                          "related": {
                            "description": "Related link.",
                            "example": "/api/v2/team/c75a4a8e-20c7-11ee-a3a5-da7ad0900002/links",
                            "type": "string"
                          }
                        },
                        "type": "object"
                      }
                    },
                    "type": "object"
                  }
                },
                "type": "object"
              },
              "type": {
                "default": "team",
                "description": "Team type",
                "enum": [
                  "team"
                ],
                "example": "team",
                "type": "string",
                "x-enum-varnames": [
                  "TEAM"
                ]
              }
            },
            "required": [
              "attributes",
              "type"
            ],
            "type": "object"
          }
        },
        "required": [
          "data"
        ],
        "type": "object"
      }
    }
  },
  "required": true
}
