http_endpoint.request_body_spec:
{
  "description": "Department that needs to be created",
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "id": {
            "type": "integer",
            "description": "Unique identifier of the department",
            "format": "int64",
            "readOnly": true,
            "example": 14000234324
          },
          "name": {
            "type": "string",
            "description": "Name of the department",
            "example": "IT Support"
          },
          "description": {
            "type": "string",
            "description": "Description about the department",
            "example": "IT Support"
          },
          "head_user_id": {
            "type": "integer",
            "description": "Unique identifier of the agent or requester who serves as the head of the department",
            "format": "int64",
            "example": 14000234324
          },
          "prime_user_id": {
            "type": "integer",
            "description": "Unique identifier of the agent or requester who serves as the prime user of the department",
            "format": "int64",
            "example": 14000234324
          },
          "domains": {
            "type": "array",
            "description": "Email domains associated with the department",
            "example": [
              "support.freshservice.com"
            ],
            "items": {
              "type": "string"
            }
          },
          "custom_fields": {
            "type": "object",
            "description": "Custom fields that are associated with a Freshservice entity",
            "example": {
              "field1": "Value 1"
            }
          },
          "created_at": {
            "type": "string",
            "description": "Timestamp at which the department was created",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-11-22T16:58:45Z"
          },
          "updated_at": {
            "type": "string",
            "description": "Timestamp at which the department was last modified",
            "format": "date-time",
            "readOnly": true,
            "example": "2021-11-22T16:58:45Z"
          }
        }
      }
    }
  },
  "required": true
}
