http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "properties": {
          "method": {
            "description": "The domain operation to perform. It can be either `add` or `move-in`.",
            "type": "string",
            "example": "add"
          }
        },
        "oneOf": [
          {
            "additionalProperties": false,
            "type": "object",
            "description": "add",
            "required": [
              "name"
            ],
            "properties": {
              "name": {
                "description": "The domain name you want to add.",
                "type": "string",
                "example": "example.com"
              },
              "cdnEnabled": {
                "description": "Whether the domain has the Vercel Edge Network enabled or not.",
                "type": "boolean",
                "example": true
              },
              "zone": {
                "type": "boolean"
              },
              "method": {
                "description": "The domain operation to perform.",
                "type": "string",
                "example": "add"
              }
            }
          },
          {
            "additionalProperties": false,
            "type": "object",
            "description": "move-in",
            "required": [
              "method",
              "name"
            ],
            "properties": {
              "name": {
                "description": "The domain name you want to add.",
                "type": "string",
                "example": "example.com"
              },
              "method": {
                "description": "The domain operation to perform.",
                "type": "string",
                "example": "move-in"
              },
              "token": {
                "description": "The move-in token from Move Requested email.",
                "type": "string",
                "example": "fdhfr820ad#@FAdlj$$"
              }
            }
          },
          {
            "deprecated": true,
            "additionalProperties": false,
            "type": "object",
            "description": "transfer-in",
            "required": [
              "method",
              "name"
            ],
            "properties": {
              "name": {
                "description": "The domain name you want to add.",
                "type": "string",
                "example": "example.com"
              },
              "method": {
                "description": "The domain operation to perform.",
                "type": "string",
                "example": "transfer-in"
              },
              "authCode": {
                "description": "The authorization code assigned to the domain.",
                "type": "string",
                "example": "fdhfr820ad#@FAdlj$$"
              },
              "expectedPrice": {
                "description": "The price you expect to be charged for the required 1 year renewal.",
                "type": "number",
                "example": 8
              }
            }
          }
        ]
      }
    }
  }
}
