          "event"
        ],
        "properties": {
          "event": {
            "oneOf": [
              {
                "type": "object",
                "properties": {
                  "type": {
                    "type": "string",
                    "enum": [
                      "installation.updated"
                    ]
                  },
                  "billingPlanId": {
                    "type": "string",
                    "description": "The installation-level billing plan ID"
                  }
                },
                "required": [
                  "type"
                ],
                "additionalProperties": false
              },
              {
                "type": "object",
                "properties": {
                  "type": {
                    "type": "string",
                    "enum": [
                      "resource.updated"
                    ]
                  },
                  "productId": {
                    "type": "string",
                    "description": "Partner-provided product slug or id"
                  },
                  "resourceId": {
                    "type": "string",
                    "description": "Partner provided resource ID"
                  }
                },
                "required": [
                  "type",
                  "productId",
                  "resourceId"
                ],
                "additionalProperties": false
              }
            ]
          }
        },
        "additionalProperties": false
      }
    }
  },
  "required": true
}
