http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "additionalProperties": false,
        "properties": {
          "name": {
            "type": "string",
            "description": "The name of the access group",
            "maxLength": 50,
            "pattern": "^[A-z0-9_ -]+$",
            "example": "My access group"
          },
          "projects": {
            "type": "array",
            "items": {
              "type": "object",
              "additionalProperties": false,
              "required": [
                "role",
                "projectId"
              ],
              "properties": {
                "projectId": {
                  "type": "string",
                  "maxLength": 256,
                  "example": "prj_ndlgr43fadlPyCtREAqxxdyFK",
                  "description": "The ID of the project."
                },
                "role": {
                  "type": "string",
                  "enum": [
                    "ADMIN",
                    "PROJECT_VIEWER",
                    "PROJECT_DEVELOPER",
                    null
                  ],
                  "example": "ADMIN",
                  "description": "The project role that will be added to this Access Group. \\\"null\\\" will remove this project level role.",
                  "nullable": true
                }
              }
            }
          },
          "membersToAdd": {
            "description": "List of members to add to the access group.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "usr_1a2b3c4d5e6f7g8h9i0j",
              "usr_2b3c4d5e6f7g8h9i0j1k"
            ]
          },
          "membersToRemove": {
            "description": "List of members to remove from the access group.",
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "usr_1a2b3c4d5e6f7g8h9i0j",
              "usr_2b3c4d5e6f7g8h9i0j1k"
            ]
          }
        }
      }
    }
  },
  "required": true
}
