http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "additionalProperties": false,
        "properties": {
          "name": {
            "type": "string",
            "description": "The name of the DNS record",
            "example": "example-1",
            "nullable": true
          },
          "value": {
            "type": "string",
            "description": "The value of the DNS record",
            "example": "google.com",
            "nullable": true
          },
          "type": {
            "enum": [
              "A",
              "AAAA",
              "ALIAS",
              "CAA",
              "CNAME",
              "HTTPS",
              "MX",
              "SRV",
              "TXT",
              "NS"
            ],
            "type": "string",
            "description": "The type of the DNS record",
            "example": "A",
            "maxLength": 255,
            "nullable": true
          },
          "ttl": {
            "type": "integer",
            "description": "The Time to live (TTL) value of the DNS record",
            "example": "60",
            "minimum": 60,
            "maximum": 2147483647,
            "nullable": true
          },
          "mxPriority": {
            "type": "integer",
            "description": "The MX priority value of the DNS record",
            "nullable": true
          },
          "srv": {
            "additionalProperties": false,
            "required": [
              "target",
              "weight",
              "port",
              "priority"
            ],
            "properties": {
              "target": {
                "type": "string",
                "description": "",
                "example": "example2.com.",
                "maxLength": 255,
                "nullable": true
              },
              "weight": {
                "description": "",
                "type": "integer",
                "nullable": true
              },
              "port": {
                "description": "",
                "type": "integer",
                "nullable": true
              },
              "priority": {
                "description": "",
                "type": "integer",
                "nullable": true
              }
            },
            "type": "object",
            "nullable": true
          },
          "https": {
            "additionalProperties": false,
            "required": [
              "priority",
              "target"
            ],
            "properties": {
              "priority": {
                "description": "",
                "type": "integer",
                "nullable": true
              },
              "target": {
                "type": "string",
                "description": "",
                "example": "example2.com.",
                "maxLength": 255,
                "nullable": true
              },
              "params": {
                "description": "",
                "type": "string",
                "nullable": true
              }
            },
            "type": "object",
            "nullable": true
          },
          "comment": {
            "type": "string",
            "description": "A comment to add context on what this DNS record is for",
            "example": "used to verify ownership of domain",
            "maxLength": 500
          }
        },
        "type": "object"
      }
    }
  },
  "required": true
}
