http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "customer_id",
          "date",
          "creditnote_number",
          "line_items"
        ],
        "type": "object",
        "properties": {
          "customer_id": {
            "description": "Unique identifier for the customer contact. Retrieve customer IDs from the Get Customers API endpoint in the contacts section. This ID links the credit note to the specific customer record.",
            "type": "string",
            "example": "903000000000099"
          },
          "currency_id": {
            "description": "Unique identifier for the currency used in the credit note. Can differ from customer currency if multi-currency is enabled. Retrieve currency IDs from the List Currencies API endpoint in the currency section.",
            "type": "string",
            "example": "982000000567240"
          },
          "contact_persons_associated": {
            "description": "Contact Persons associated with the transaction.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "contact_person_id": {
                  "description": "Unique ID of the Contact Person.",
                  "type": "integer",
                  "format": "int64",
                  "example": 982000000567003
                },
                "communication_preference": {
                  "description": "Preferred modes of communication for the contact person at transaction level.",
                  "type": "object",
                  "properties": {
                    "is_email_enabled": {
                      "description": "Used to check if Email communication preference is enabled for the contact person at transaction level.",
                      "type": "boolean",
                      "example": true
                    },
                    "is_whatsapp_enabled": {
                      "description": "Used to check if WhatsApp communication preference is enabled for the contact person at transaction level.",
                      "type": "boolean",
                      "example": true,
                      "x-node_available_in": [
                        "WhatsApp integration"
                      ]
                    }
                  }
                }
              }
            }
          },
          "date": {
            "description": "The date on which the credit note is raised. Must be in yyyy-mm-dd format. This date determines the accounting period and affects tax calculations.",
            "type": "string",
            "example": "2016-06-05"
          },
          "is_draft": {
            "description": "Set to true to create the credit note in draft status. Draft credit notes are not finalized and can be modified before sending to customers.",
            "type": "boolean",
            "example": true
          },
          "exchange_rate": {
            "description": "Exchange rate for converting the credit note currency to base currency. Required when currency differs from base currency. Retrieve rates from currency exchange rate APIs.",
            "type": "string",
            "example": "5.5"
          },
          "line_items": {
            "description": "Array of line items for the credit note. Each line item represents a product or service being credited to the customer.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "item_id": {
                  "description": "Unique identifier for the item in the line item. Retrieve item IDs from the List Items API endpoint in the items section. Links the line item to a specific product or service.",
                  "type": "string",
                  "example": "90300000081501"
                },
                "invoice_id": {
                  "description": "Invoice ID of the required invoice.",
                  "type": "string",
                  "example": "90300000079426"
                },
                "invoice_item_id": {
                  "description": "Unique identifier of the invoice line item that this credit note line item references. Use this when creating a credit note for a specific invoice line item.",
                  "type": "string",
                  "example": "982000000567021"
                },
                "description": {
                  "description": "Detailed description of the line item. Provides additional information about the product or service being credited. Displayed on the credit note document.",
                  "type": "string",
                  "example": "prorated amount for items"
                },
                "code": {
                  "description": "Unique code or SKU for the line item. Used for product identification and inventory tracking. Helps distinguish between different variants of the same product.",
                  "type": "string",
                  "example": "basic-monthly"
                },
                "name": {
                  "description": "Name or title of the line item. Describes the product or service being credited. This name is displayed on the credit note document.",
                  "type": "string",
                  "example": "Basic Monthly"
                },
                "type": {
                  "description": "Type of the credit note line item. Numeric value indicating the line item type (e.g., 1 for product, 2 for service). Used for categorization and reporting purposes.",
                  "type": "integer",
                  "example": 1
                },
                "account_id": {
                  "description": "Unique identifier for the chart of accounts entry. Retrieve account IDs from the List Chart of Accounts API endpoint. Determines which account the line item affects for accounting purposes.",
                  "type": "string",
                  "example": "903000000000388"
                },
                "account_name": {
                  "description": "Name of the chart of accounts entry. Displays the account name for reference and reporting purposes. Should match the account_id provided.",
                  "type": "string",
                  "example": "Sales"
                },
                "quantity": {
                  "description": "Number of units being credited for this line item. Must be a positive number. Used to calculate the total amount for the line item.",
                  "type": "integer",
                  "format": "int32",
                  "example": 1
                },
                "tax_id": {
                  "description": "Unique identifier for the tax applied to the line item. Retrieve tax IDs from the List Taxes API endpoint in the taxes section. Determines tax calculations for this specific line item.",
                  "type": "string",
                  "example": "903000000000356"
                },
                "tds_tax_id": {
                  "description": "Unique ID to denote the TDS tax associated to the creditnote",
                  "type": "string",
                  "example": "903000000000357",
                  "x-node_available_in": [
                    "mx"
                  ],
                  "x-node_unavailable_in": []
                },
                "tax_treatment_code": {
                  "description": "Specify reason for using out of scope.</br>Supported values for UAE are <code>uae_same_tax_group</code>, <code>uae_reimbursed_expense</code> and <code>uae_others</code>.</br> Supported values for Bahrain are <code>bahrain_same_tax_group</code>, <code>bahrain_transfer_of_concern</code>, <code>bahrain_disbursement</code>, <code>bahrain_head_to_branch_transaction</code>, <code>bahrain_warranty_repair_services</code> and <code>bahrain_others</code>.</br>Supported values for Saudi Arabia are <code>ksa_pvt_health</code>, <code>ksa_pvt_edu</code>, <code>ksa_reimbursed_expense</code> and <code>ksa_house_sales</code>.",
                  "type": "string",
                  "example": "uae_others",
                  "x-node_available_in": [
                    "gcc"
                  ],
                  "x-node_unavailable_in": []
                },
                "product_type": {
                  "description": "Type of product or service for the line item. Standard values: <code>goods</code> or <code>services</code>. For South Africa: <code>service</code>, <code>goods</code>, <code>capital_service</code>, and <code>capital_goods</code>.",
                  "type": "string",
                  "example": "goods"
                },
                "sat_item_key_code": {
                  "description": "Add SAT Item Key Code for your goods/services. Download the <a href= http://omawww.sat.gob.mx/tramitesyservicios/Paginas/documentos/catCFDI_V_4_07122022.xls  >CFDI Catalogs.</a>",
                  "type": "string",
                  "example": 71121206,
                  "x-node_available_in": [
                    "mx"
                  ],
                  "x-node_unavailable_in": []
                },
                "unitkey_code": {
                  "description": "Add Unit Key Code for your goods/services. Download the <a href= http://omawww.sat.gob.mx/tramitesyservicios/Paginas/documentos/catCFDI_V_4_07122022.xls  >CFDI Catalogs.</a>",
                  "type": "string",
                  "example": "box",
                  "x-node_available_in": [
                    "mx"
                  ],
                  "x-node_unavailable_in": []
                },
                "serial_numbers": {
                  "description": "Serial numbers of the items being credited. Used for tracking specific units of inventory or equipment. Important for warranty and return tracking.",
                  "type": "string"
                },
                "location_id": {
                  "description": "Unique identifier for the business location associated with the line item. Retrieve location IDs from the List Locations API endpoint in the locations section. Determines tax settings and business rules for this line item.",
                  "type": "string",
                  "example": "460000000038080"
                },
                "project_id": {
                  "description": "Unique identifier for the project associated with the line item. Links the credit to a specific project for project-based accounting and reporting.",
                  "type": "string",
                  "example": 90300000087378
                }
              }
            }
          },
          "location_id": {
            "description": "Unique identifier for the business location associated with the line item. Retrieve location IDs from the List Locations API endpoint in the locations section. Determines tax settings and business rules for this line item.",
            "type": "string",
            "example": "460000000038080"
          },
          "creditnote_number": {
            "description": "Unique credit note number displayed in the interface. Auto-generated with CN prefix unless ignore_auto_number_generation is true. Max-Length [100]",
            "type": "string",
            "example": "CN-29"
          },
          "gst_treatment": {
            "description": "Choose whether the contact is GST registered/unregistered/consumer/overseas. Allowed values are <code> business_gst </code> , <code> business_none </code> , <code> overseas </code> , <code> consumer </code>.",
            "type": "string",
            "example": "business_gst",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "tax_treatment": {
            "description": "VAT treatment for the credit note .Choose whether the contact falls under: <code>vat_registered</code>,<code>vat_not_registered</code>,<code>gcc_vat_not_registered</code>,<code>gcc_vat_registered</code>,<code>non_gcc</code>.</br><code>dz_vat_registered</code> and <code>dz_vat_not_registered</code> supported only for <b>UAE</b>.</b></br><code>home_country_mexico</code>,<code>border_region_mexico</code>,<code>non_mexico</code> supported only for <b>MX</b>. <br/><b>For Kenya Edition:</b> <code>vat_registered</code> ,<code>vat_not_registered</code> ,<code>non_kenya</code>(A business that is located outside Kenya).<br/><b>For SouthAfrica Edition:</b> <code>vat_registered</code>, <code>vat_not_registered</code>, <code>overseas</code>(A business that is located outside SouthAfrica).",
            "type": "string",
            "example": "vat_registered",
            "x-node_available_in": [
              "gcc",
              "mx",
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "is_reverse_charge_applied": {
            "description": "(Required if customer tax treatment is <code>vat_registered</code>)<br/>Used to specify whether the transaction is applicable for Domestic Reverse Charge (DRC) or not.",
            "type": "boolean",
            "example": true,
            "x-node_available_in": [
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "gst_no": {
            "description": "15 digit GST identification number of the customer.",
            "type": "string",
            "example": "22AAAAA0000A1Z5",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "cfdi_usage": {
            "description": "Choose CFDI Usage.</br>Allowed values: <code>acquisition_of_merchandise</code>, <code>return_discount_bonus</code>, <code>general_expense</code>, <code>buildings</code>, <code>furniture_office_equipment</code>, <code>transport_equipment</code>, <code>computer_equipmentdye_molds_tools</code>, <code>telephone_communication</code>, <code>satellite_communication</code>, <code>other_machinery_equipment</code>, <code>hospital_expense</code>, <code>medical_expense_disability</code>, <code>funeral_expense</code>, <code>donation</code>, <code>interest_mortage_loans</code>, <code>contribution_sar</code>, <code>medical_expense_insurance_pormium</code>, <code>school_transportation_expense</code>, <code>deposit_saving_account</code>, <code>payment_educational_service</code>, <code>no_tax_effect</code>, <code>payment</code>, <code>payroll</code>.",
            "type": "string",
            "example": "acquisition_of_merchandise",
            "x-node_available_in": [
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "cfdi_reference_type": {
            "description": "Choose CFDI Reference Type.</br>Allowed values: <code>credit_note</code>, <code>debit_note</code>, <code>return_of_merchandise</code>, <code>substitution_previous_cfdi</code>, <code>transfer_of_goods</code>, <code>invoice_generated_from_order</code>, <code>cfdi_for_advance</code>.",
            "type": "string",
            "example": "return_of_merchandise",
            "x-node_available_in": [
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "place_of_supply": {
            "description": "Place where the goods/services are supplied to. (If not given, <code>place of contact</code> given for the contact will be taken)<br/>Supported codes for UAE emirates are : </br>Abu Dhabi - <code>AB</code>,</br>Ajman - <code>AJ</code>,</br>Dubai - <code>DU</code>,</br>Fujairah - <code>FU</code>,</br>Ras al-Khaimah - <code>RA</code>,</br>Sharjah - <code>SH</code>,</br>Umm al-Quwain - <code>UM</code><br/>Supported codes for the GCC countries are : </br>United Arab Emirates - <code>AE</code>,</br>Saudi Arabia - <code>SA</code>,</br>Bahrain - <code>BH</code>,</br>Kuwait - <code>KW</code>,</br>Oman - <code>OM</code>,</br>Qatar - <code>QA</code>.",
            "type": "string",
            "example": "TN",
            "x-node_available_in": [
              "in",
              "gcc"
            ],
            "x-node_unavailable_in": []
          },
          "ignore_auto_number_generation": {
            "description": "Set to true to provide a custom credit note number instead of auto-generated CN prefix. When true, you must specify creditnote_number manually.",
            "type": "boolean",
            "example": false
          },
          "reference_number": {
            "description": "Optional reference number for tracking purposes. Can be any custom string or auto-generated. Useful for linking to external systems or internal tracking. Max-Length [100]",
            "type": "string",
            "example": "INV-384"
          },
          "custom_fields": {
            "description": "Additional fields for the Credit-Notes.",
            "type": "array",
            "items": {
              "type": "string"
            }
          },
          "notes": {
            "description": "Additional notes or comments for the credit note. Displayed on the credit note document for customer reference. Max-length [5000]",
            "type": "string",
            "example": "Offer for the referral"
          },
          "terms": {
            "description": "Terms and conditions displayed on the credit note document. Include payment terms, return policies, or other business conditions. Max-length [10000]",
            "type": "string",
            "example": ""
          },
          "template_id": {
            "description": "Unique identifier for the credit note PDF template. Retrieve template IDs from the List Credit Note Templates API endpoint. Determines the visual layout and branding of the credit note document.",
            "type": "string",
            "example": "90300000001336"
          },
          "tax_id": {
            "description": "Unique identifier for the tax applied to the line item. Retrieve tax IDs from the List Taxes API endpoint in the taxes section. Determines tax calculations for this specific line item.",
            "type": "string",
            "example": "903000000000356"
          },
          "tax_authority_id": {
            "description": "Unique ID of the tax authority. Tax authority depends on the location of the customer. For example, if the customer is located in NY, then the tax authority is NY tax authority.",
            "type": "string",
            "example": "903000006345",
            "x-node_available_in": [
              "us"
            ],
            "x-node_unavailable_in": []
          },
          "tax_exemption_id": {
            "description": "Unique ID of the tax exemption.",
            "type": "string",
            "example": "903000006345",
            "x-node_available_in": [
              "in",
              "us",
              "au",
              "ca"
            ],
            "x-node_unavailable_in": []
          },
          "avatax_use_code": {
            "description": "Used to group like customers for exemption purposes. It is a custom value that links customers to a tax rule. Select from Avalara [standard codes][1] or enter a custom code. Max-length [25]",
            "type": "string",
            "x-node_available_in": [
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          },
          "avatax_exempt_no": {
            "description": "Exemption certificate number of the customer. Max-length [25]",
            "type": "string",
            "x-node_available_in": [
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          },
          "vat_treatment": {
            "description": "(Optional) VAT treatment for the invoices. VAT treatment denotes the location of the customer, if the customer resides in UK then the VAT treatment is <code>uk</code>. If the customer is in an EU country & VAT registered, you are resides in Northen Ireland and selling Goods then his VAT treatment is <code>eu_vat_registered</code>, if he resides outside of the UK then his VAT treatment is <code>overseas</code> (For Pre Brexit, this can be split as <code>eu_vat_registered</code>, <code>eu_vat_not_registered</code> and <code>non_eu</code>).",
            "type": "string",
            "example": "overseas",
            "x-node_available_in": [
              "uk"
            ],
            "x-node_unavailable_in": []
          },
          "is_inclusive_tax": {
            "description": "Used to specify whether the line item rates are inclusive or exclusive of tax.",
            "type": "boolean",
            "example": "fasle",
            "x-node_available_in": [],
            "x-node_unavailable_in": [
              "us",
              "ca"
            ]
          },
          "avatax_tax_code": {
            "description": "A tax code is a unique label used to group Items (products, services, or charges) together. Refer the [link][2] for more deails. Max-length [25]",
            "type": "string",
            "x-node_available_in": [
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          }
        }
      }
    }
  }
}
