http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "customer_id"
        ],
        "type": "object",
        "properties": {
          "customer_id": {
            "description": "Unique identifier for the customer receiving the sales order. This ID links the order to a specific customer account and determines billing information, payment terms, pricing rules, and tax calculations based on customer settings. Can be fetched from the Get Contacts API or retrieved from existing customer records in your organization.",
            "type": "string",
            "example": "460000000017138"
          },
          "currency_id": {
            "description": "Unique identifier for the currency used in the sales order transaction. Determines pricing display, exchange rate calculations, and affects financial reporting. If not specified, the organization default currency will be used automatically. Can be fetched from the Get Currencies API or retrieved from your organization currency settings.",
            "type": "string",
            "example": "460000000000097"
          },
          "contact_persons_associated": {
            "description": "Contact Persons associated with the transaction.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "contact_person_id": {
                  "description": "Unique ID of the Contact Person.",
                  "type": "integer",
                  "format": "int64",
                  "example": 982000000567003
                },
                "communication_preference": {
                  "description": "Preferred modes of communication for the contact person at transaction level.",
                  "type": "object",
                  "properties": {
                    "is_email_enabled": {
                      "description": "Used to check if Email communication preference is enabled for the contact person at transaction level.",
                      "type": "boolean",
                      "example": true
                    },
                    "is_whatsapp_enabled": {
                      "description": "Used to check if WhatsApp communication preference is enabled for the contact person at transaction level.",
                      "type": "boolean",
                      "example": true,
                      "x-node_available_in": [
                        "WhatsApp integration"
                      ]
                    }
                  }
                }
              }
            }
          },
          "date": {
            "description": "Date when the sales order was created in YYYY-MM-DD format. This date affects order processing timelines, delivery schedules, payment terms calculation, and appears on customer-facing documents and internal reports.",
            "type": "string",
            "example": "2014-07-28"
          },
          "shipment_date": {
            "description": "Expected or actual date when goods will be shipped to the customer in YYYY-MM-DD format. Used for delivery planning, customer communication, inventory management, and tracking order fulfillment timelines.",
            "type": "string"
          },
          "custom_fields": {
            "description": "Custom fields for a sales order.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "customfield_id": {
                  "description": "ID of the Custom Field",
                  "type": "integer",
                  "format": "int64",
                  "example": "460000000639129"
                },
                "value": {
                  "description": "Value of the Custom Field",
                  "type": "string",
                  "example": "Normal"
                }
              }
            }
          },
          "place_of_supply": {
            "description": "Place where the goods/services are supplied to. (If not given, <code>place of contact</code> given for the contact will be taken)<br/>Supported codes for UAE emirates are : </br>Abu Dhabi - <code>AB</code>,</br>Ajman - <code>AJ</code>,</br>Dubai - <code>DU</code>,</br>Fujairah - <code>FU</code>,</br>Ras al-Khaimah - <code>RA</code>,</br>Sharjah - <code>SH</code>,</br>Umm al-Quwain - <code>UM</code><br/>Supported codes for the GCC countries are : </br>United Arab Emirates - <code>AE</code>,</br>Saudi Arabia - <code>SA</code>,</br>Bahrain - <code>BH</code>,</br>Kuwait - <code>KW</code>,</br>Oman - <code>OM</code>,</br>Qatar - <code>QA</code>.",
            "type": "string",
            "example": "TN",
            "x-node_available_in": [
              "in",
              "gcc"
            ],
            "x-node_unavailable_in": []
          },
          "salesperson_id": {
            "description": "Unique identifier for the salesperson responsible for this sales order. Links the order to a specific sales representative customer relationship management, and sales reporting purposes.",
            "type": "string",
            "example": "460000000000097"
          },
          "merchant_id": {
            "description": "ID of the merchant",
            "type": "string",
            "example": "460000000000597"
          },
          "gst_treatment": {
            "description": "Choose whether the contact is GST registered/unregistered/consumer/overseas. Allowed values are <code> business_gst </code> , <code> business_none </code> , <code> overseas </code> , <code> consumer </code>.",
            "type": "string",
            "example": "business_gst",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "gst_no": {
            "description": "15 digit GST identification number of the customer.",
            "type": "string",
            "example": "22AAAAA0000A1Z5",
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "is_inclusive_tax": {
            "description": "Used to specify whether the line item rates are inclusive or exclusive of tax.",
            "type": "boolean",
            "example": false,
            "x-node_available_in": [],
            "x-node_unavailable_in": [
              "us",
              "ca"
            ]
          },
          "location_id": {
            "description": "Unique identifier for the business location entity. Specifies the organizational location where the sales order transaction is processed, affecting tax jurisdiction calculations, inventory allocation, reporting segmentation, and location-specific business rules enforcement. Can be retrieved from the Locations API.",
            "type": "string",
            "example": "460000000038080"
          },
          "line_items": {
            "description": "Line items of a sales order.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "item_order": {
                  "type": "integer",
                  "example": 0
                },
                "item_id": {
                  "description": "ID of the item.",
                  "type": "string",
                  "example": "460000000017088"
                },
                "rate": {
                  "description": "Rate of the line item.",
                  "type": "number",
                  "format": "double",
                  "example": 120
                },
                "name": {
                  "description": "Name of the line item.",
                  "type": "string",
                  "example": "Hard Drive"
                },
                "description": {
                  "description": "Description of the line item.",
                  "type": "string",
                  "example": "500GB, USB 2.0 interface 1400 rpm, protective hard case."
                },
                "quantity": {
                  "description": "Quantity of the line item.",
                  "type": "number",
                  "format": "double",
                  "example": 40
                },
                "product_type": {
                  "description": "Enter <code>goods</code> or <code>services</code> .<br/><b>For SouthAfrica Edition:</b> <code>service</code>, <code>goods</code>, <code>capital_service</code> and <code>capital_goods</code>",
                  "type": "string",
                  "example": "goods"
                },
                "hsn_or_sac": {
                  "description": "Add HSN/SAC code for your goods/services",
                  "type": "string",
                  "example": 80540,
                  "x-node_available_in": [
                    "in",
                    "za",
                    "ke"
                  ],
                  "x-node_unavailable_in": []
                },
                "sat_item_key_code": {
                  "description": "Add SAT Item Key Code for your goods/services. Download the <a href= http://omawww.sat.gob.mx/tramitesyservicios/Paginas/documentos/catCFDI_V_4_07122022.xls  >CFDI Catalogs.</a>",
                  "type": "string",
                  "example": 71121206,
                  "x-node_available_in": [
                    "mx"
                  ],
                  "x-node_unavailable_in": []
                },
                "unitkey_code": {
                  "description": "Add SAT Unit Key Code for your goods/services. Download the <a href= http://omawww.sat.gob.mx/tramitesyservicios/Paginas/documentos/catCFDI_V_4_07122022.xls  >CFDI Catalogs.</a>",
                  "type": "string",
                  "example": "E48",
                  "x-node_available_in": [
                    "mx"
                  ],
                  "x-node_unavailable_in": []
                },
                "location_id": {
                  "description": "Unique identifier for the business location entity. Specifies the organizational location where the sales order transaction is processed, affecting tax jurisdiction calculations, inventory allocation, reporting segmentation, and location-specific business rules enforcement. Can be retrieved from the Locations API.",
                  "type": "string",
                  "example": "460000000038080"
                },
                "discount": {
                  "description": "Discount value applied to the sales order total, specified as either percentage or absolute amount. Supports decimal precision and affects final pricing calculations, tax computations, and financial reporting. Format: percentage (e.g., \"12.5%\") or amount (e.g., \"190\").",
                  "type": "string"
                },
                "tax_id": {
                  "description": "ID of the tax or tax group applied",
                  "type": "string",
                  "example": "460000000017094"
                },
                "tds_tax_id": {
                  "description": "ID of the TDS tax or TDS tax group applied",
                  "type": "string",
                  "example": "460000000017098",
                  "x-node_available_in": [
                    "mx"
                  ],
                  "x-node_unavailable_in": []
                },
                "tags": {
                  "description": "Filter all your reports based on the tag",
                  "type": "array",
                  "items": {
                    "type": "object",
                    "properties": {
                      "tag_id": {
                        "description": "ID of the reporting tag",
                        "type": "string",
                        "example": 462000000009070
                      },
                      "tag_option_id": {
                        "description": "ID of the reporting tag's option",
                        "type": "string",
                        "example": 462000000002670
                      }
                    }
                  }
                },
                "unit": {
                  "description": "Unit of the line item e.g. kgs, Nos.",
                  "type": "string",
                  "example": "Nos"
                },
                "item_custom_fields": {
                  "description": "Custom fields for a sales order items.",
                  "type": "array",
                  "items": {
                    "type": "object",
                    "properties": {
                      "customfield_id": {
                        "description": "ID of the Custom Field",
                        "type": "integer",
                        "format": "int64",
                        "example": "460000000639129"
                      },
                      "value": {
                        "description": "Value of the Custom Field",
                        "type": "string",
                        "example": "Normal"
                      }
                    }
                  }
                },
                "tax_exemption_id": {
                  "description": "ID of the tax exemption applied",
                  "type": "string",
                  "x-node_available_in": [
                    "in",
                    "us"
                  ],
                  "x-node_unavailable_in": []
                },
                "tax_exemption_code": {
                  "description": "Code of Tax Exemption that is applied",
                  "type": "string",
                  "x-node_available_in": [
                    "in",
                    "us",
                    "mx"
                  ],
                  "x-node_unavailable_in": []
                },
                "tax_treatment_code": {
                  "description": "Specify reason for using out of scope.</br>Supported values for <b>UAE</b> are <code>uae_same_tax_group</code>, <code>uae_reimbursed_expense</code> and <code>uae_others</code>.</br> Supported values for <b>Bahrain</b> are <code>bahrain_same_tax_group</code>, <code>bahrain_transfer_of_concern</code>, <code>bahrain_disbursement</code>, <code>bahrain_head_to_branch_transaction</code>, <code>bahrain_warranty_repair_services</code> and <code>bahrain_others</code>.</br>Supported values for <b>Saudi Arabia</b> are <code>ksa_pvt_health</code>, <code>ksa_pvt_edu</code>, <code>ksa_reimbursed_expense</code> and <code>ksa_house_sales</code>.",
                  "type": "string",
                  "example": "uae_others",
                  "x-node_available_in": [
                    "gcc"
                  ],
                  "x-node_unavailable_in": []
                },
                "avatax_exempt_no": {
                  "description": "Exemption certificate number of the customer.",
                  "type": "string",
                  "x-node_available_in": [
                    "Avalara Integration"
                  ],
                  "x-node_unavailable_in": []
                },
                "avatax_use_code": {
                  "description": "Used to group like customers for exemption purposes. It is a custom value that links customers to a tax rule.",
                  "type": "string",
                  "x-node_available_in": [
                    "Avalara Integration"
                  ],
                  "x-node_unavailable_in": []
                },
                "project_id": {
                  "description": "ID of the project",
                  "type": "string",
                  "example": 90300000087378
                }
              }
            }
          },
          "notes": {
            "description": "Free-form text field containing additional information, instructions, or comments related to the sales order. This field supports internal documentation, customer-specific requirements, special handling instructions, and inter-departmental communication for order processing workflows.",
            "type": "string"
          },
          "terms": {
            "description": "Contractual terms and conditions governing the sales order transaction. Defines payment obligations, delivery specifications, warranty provisions, liability limitations, and other legal stipulations that establish the binding agreement between the organization and customer for this specific transaction.",
            "type": "string"
          },
          "billing_address_id": {
            "description": "Unique identifier referencing the customer billing address entity. Determines the address used for invoice generation, payment processing, tax jurisdiction calculations, and financial record-keeping. Must correspond to a valid address record associated with the specified customer account.",
            "type": "string",
            "example": 460000000032174
          },
          "shipping_address_id": {
            "description": "Unique identifier referencing the customer shipping address entity. Specifies the delivery destination for goods fulfillment, logistics planning, shipping cost calculations, and delivery confirmation processes. Must correspond to a valid address record within the customer address registry.",
            "type": "string"
          },
          "crm_owner_id": {
            "type": "string"
          },
          "crm_custom_reference_id": {
            "type": "string"
          },
          "vat_treatment": {
            "description": "(Optional) VAT treatment for the sales order. VAT treatment denotes the location of the customer, if the customer resides in UK then the VAT treatment is <code>uk</code>. If the customer is in an EU country & VAT registered, you are resides in Northen Ireland and purchasing Goods then his VAT treatment is <code>eu_vat_registered</code> and if he resides outside the UK then his VAT treatment is <code>overseas</code>(For Pre Brexit, this can be split as <code>eu_vat_registered</code>, <code>eu_vat_not_registered</code> and <code>non_eu</code>).",
            "type": "string",
            "x-node_available_in": [
              "uk"
            ],
            "x-node_unavailable_in": []
          },
          "tax_treatment": {
            "description": "VAT treatment for the sales order.Allowed Values: <code>vat_registered</code>,<code>vat_not_registered</code>,<code>gcc_vat_not_registered</code>,<code>gcc_vat_registered</code>,<code>non_gcc</code>.</br><code>dz_vat_registered</code> and <code>dz_vat_not_registered</code> supported only for <b>UAE</b>.</b></br><code>home_country_mexico</code>,<code>border_region_mexico</code>,<code>non_mexico</code> supported only for <b>MX</b>. <br/><b>For Kenya Edition:</b> <code>vat_registered</code> ,<code>vat_not_registered</code> ,<code>non_kenya</code>(A business that is located outside Kenya).<br/><b>For SouthAfrica Edition:</b> <code>vat_registered</code>, <code>vat_not_registered</code>, <code>overseas</code>(A business that is located outside SouthAfrica).",
            "type": "string",
            "example": "vat_registered",
            "x-node_available_in": [
              "gcc",
              "mx",
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "is_reverse_charge_applied": {
            "description": "(Required if customer tax treatment is <code>vat_registered</code>)<br/>Used to specify whether the transaction is applicable for Domestic Reverse Charge (DRC) or not.",
            "type": "boolean",
            "example": true,
            "x-node_available_in": [
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "salesorder_number": {
            "description": "Unique alphanumeric identifier for the sales order entity. Required when automatic numbering is disabled in organization settings. Must be unique within the organization and follows the configured numbering sequence pattern for sales order identification and tracking.",
            "type": "string",
            "example": "SO-00001"
          },
          "reference_number": {
            "description": "External reference identifier for cross-referencing with customer purchase orders, internal tracking systems, or third-party applications. Used for order reconciliation, customer communication, and integration with external business systems and workflows.",
            "type": "string",
            "example": "REF-001"
          },
          "is_update_customer": {
            "description": "Boolean flag indicating whether customer billing address information should be synchronized with the sales order data. When enabled, updates the customer master record with address modifications, ensuring data consistency across customer relationship management systems.",
            "type": "boolean",
            "example": false
          },
          "discount": {
            "description": "Discount value applied to the sales order total, specified as either percentage or absolute amount. Supports decimal precision and affects final pricing calculations, tax computations, and financial reporting. Format: percentage (e.g., \"12.5%\") or amount (e.g., \"190\").",
            "type": "string"
          },
          "exchange_rate": {
            "description": "Currency conversion rate from the transaction currency to the organization base currency. Required for multi-currency transactions to ensure accurate financial reporting, profit calculations, and tax computations in the base currency denomination.",
            "type": "number",
            "format": "double",
            "example": 1.233
          },
          "salesperson_name": {
            "description": "Full name of the sales representative assigned to this sales order. Automatically populated when salesperson_id is specified.",
            "type": "string",
            "example": "John Roberts"
          },
          "notes_default": {
            "description": "Default Notes for the Sales Order",
            "type": "string"
          },
          "terms_default": {
            "description": "Default Terms of the Sales Order",
            "type": "string"
          },
          "tax_id": {
            "description": "Tax ID for the Sales Order.",
            "type": "string",
            "example": "460000000017094"
          },
          "tax_authority_id": {
            "description": "ID of the tax authority. Tax authority depends on the location of the customer.",
            "type": "string",
            "x-node_available_in": [
              "us"
            ],
            "x-node_unavailable_in": []
          },
          "tax_exemption_id": {
            "description": "ID of the tax exemption applied",
            "type": "string",
            "x-node_available_in": [
              "in",
              "us"
            ],
            "x-node_unavailable_in": []
          },
          "tax_authority_name": {
            "description": "Tax Authority's name.",
            "type": "string",
            "x-node_available_in": [
              "us",
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "tax_exemption_code": {
            "description": "Code of Tax Exemption that is applied",
            "type": "string",
            "x-node_available_in": [
              "in",
              "us",
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "avatax_exempt_no": {
            "description": "Exemption certificate number of the customer.",
            "type": "string",
            "x-node_available_in": [
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          },
          "avatax_use_code": {
            "description": "Used to group like customers for exemption purposes. It is a custom value that links customers to a tax rule.",
            "type": "string",
            "x-node_available_in": [
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          },
          "shipping_charge": {
            "description": "Additional cost associated with product delivery and logistics services. Applied to the sales order total and affects final pricing calculations, profit margins, and customer billing. Supports decimal precision for accurate cost allocation.",
            "type": "number",
            "format": "double",
            "example": 2
          },
          "adjustment": {
            "description": "Manual modification amount applied to the sales order total for rounding corrections, special pricing arrangements, or other business-specific adjustments. Supports positive and negative values for additions or deductions from the order total.",
            "type": "number",
            "format": "double",
            "example": 0.2
          },
          "delivery_method": {
            "description": "Specification of the transportation and delivery mechanism for order fulfillment. Determines shipping costs, delivery timelines, carrier selection, and customer communication regarding order status and tracking information.",
            "type": "string",
            "example": "Air"
          },
          "estimate_id": {
            "description": "ID of the estimate associated with the Sales Order",
            "type": "string"
          },
          "is_discount_before_tax": {
            "description": "Boolean flag determining the sequence of discount application in tax calculations. When true, discount is applied before tax computation; when false, discount is applied after tax calculation. Affects final pricing accuracy and tax compliance reporting.",
            "type": "boolean",
            "example": true
          },
          "discount_type": {
            "description": "Classification of discount application scope within the sales order. <code>entity_level</code> applies discount to the entire order total, while <code>item_level</code> applies discount to individual line items. Determines discount calculation methodology and affects pricing structure.",
            "type": "string",
            "example": "entity_level"
          },
          "adjustment_description": {
            "description": "Textual explanation for the adjustment amount applied to the sales order. Provides audit trail documentation, justification for pricing modifications, and contextual information for financial reporting and compliance verification purposes.",
            "type": "string",
            "example": "Adjustment"
          },
          "pricebook_id": {
            "description": "Unique identifier for the pricing structure applied to the sales order. Determines item rates, discount rules, and pricing policies based on customer segments, volume tiers, or promotional campaigns. Can be retrieved from the Pricebooks API.",
            "type": "string"
          },
          "template_id": {
            "description": "Unique identifier for the PDF document template used for sales order presentation. Determines document layout, branding elements, formatting specifications, and visual presentation standards for customer-facing documents and printed materials.",
            "type": "string",
            "example": "460000000021001"
          },
          "documents": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "example": [
              "document_id",
              "file_name"
            ]
          },
          "zcrm_potential_id": {
            "description": "Unique identifier linking the sales order to a Zoho CRM potential record.",
            "type": "string",
            "example": "460000000033001"
          },
          "zcrm_potential_name": {
            "description": "Descriptive name of the Zoho CRM potential record associated with this sales order.",
            "type": "string"
          }
        }
      }
    }
  }
}
