http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "amount"
        ],
        "type": "object",
        "properties": {
          "vendor_id": {
            "description": "ID of the vendor associated with the Vendor Payment.",
            "type": "string",
            "example": "460000000026049"
          },
          "bills": {
            "description": "Individual bill payment details as array.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "bill_payment_id": {
                  "description": "ID of the Bill Payment",
                  "type": "string",
                  "example": "460000000053221"
                },
                "bill_id": {
                  "description": "ID of the bill the payment is to be applied.",
                  "type": "string",
                  "example": "460000000053199"
                },
                "amount_applied": {
                  "description": "Amount applied to the bill.",
                  "type": "number",
                  "format": "double",
                  "example": 150
                },
                "tax_amount_withheld": {
                  "description": "Tax Amount Withheld during Bill Payment",
                  "type": "number",
                  "format": "double",
                  "x-node_available_in": [
                    "global",
                    "in",
                    "au"
                  ],
                  "x-node_unavailable_in": []
                }
              }
            }
          },
          "date": {
            "description": "Date the payment is made.",
            "type": "string",
            "example": "2013-10-07"
          },
          "exchange_rate": {
            "description": "Exchange rate of the currency.",
            "type": "number",
            "format": "double",
            "example": 1
          },
          "amount": {
            "description": "Total Amount of Vendor Payment",
            "type": "number",
            "format": "double",
            "example": 500
          },
          "paid_through_account_id": {
            "description": "ID of the cash/ bank account from which the payment is made.",
            "type": "string",
            "example": "460000000000358"
          },
          "payment_mode": {
            "description": "Mode of Vendor Payment",
            "type": "string",
            "example": "Stripe"
          },
          "description": {
            "description": "Description for the Vendor Payment recorded.",
            "type": "string"
          },
          "reference_number": {
            "description": "Reference number for the Vendor Payment recorded.",
            "type": "string",
            "example": "REF#912300"
          },
          "check_details": {
            "type": "array",
            "items": {
              "type": "string"
            },
            "x-node_available_in": [
              "us",
              "ca"
            ],
            "x-node_unavailable_in": []
          },
          "is_paid_via_print_check": {
            "description": "Check if the Bill Payment is paid Via Print Check Option",
            "type": "boolean",
            "example": false,
            "x-node_available_in": [
              "us",
              "ca",
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "location_id": {
            "description": "Location ID",
            "type": "string",
            "example": "460000000038080"
          },
          "custom_fields": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "index": {
                  "description": "Index of the Custom Field",
                  "type": "integer"
                },
                "value": {
                  "description": "Value for the Custom Field",
                  "type": "string"
                }
              }
            }
          }
        }
      }
    }
  }
}
