http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "required": [
          "asset_name",
          "fixed_asset_type_id",
          "asset_account_id",
          "asset_cost",
          "asset_purchase_date"
        ],
        "properties": {
          "asset_name": {
            "description": "Enter the name of the fixed asset.",
            "type": "string",
            "example": "Laptop"
          },
          "fixed_asset_type_id": {
            "description": "Associate the fixed asset with a fixed asset type by specifying fixed asset type id.",
            "type": "string",
            "example": "3640355000000319008"
          },
          "asset_account_id": {
            "description": "Enter the account id to track the fixed asset.",
            "type": "string",
            "example": "3640355000000000367"
          },
          "expense_account_id": {
            "description": "Enter the account id to track the expenses associated with the asset\u2019s depreciation.The available account types are <b>Expense</b>  and <b>Other Expense</b>",
            "type": "string",
            "example": "3640355000000000421"
          },
          "depreciation_account_id": {
            "description": "Enter the account id to track the asset\u2019s depreciation over time.",
            "type": "string",
            "example": "3640355000000000367"
          },
          "depreciation_method": {
            "description": "Enter the depreciation method for your asset.The available methods are: <code>declining_method</code> and <code>straight_line</code>. <br/><b>For US and GLobal Edition:</b> <code>declining_method</code>, <code>straight_line</code>, <code>200_declining_method</code> and <code>150_declining_method</code>",
            "type": "string",
            "example": "declining_method"
          },
          "depreciation_frequency": {
            "description": "Enter the frequency of depreciation. The available frequencies are: <code>yearly</code> and <code>monthly</code>",
            "type": "string",
            "example": "yearly"
          },
          "depreciation_percentage": {
            "description": "Enter the depreciation percentage only if the entered depreciation method is a declining method. The entered percentage should be within the range of 0 to 100",
            "type": "number",
            "format": "double",
            "example": 12
          },
          "total_life": {
            "description": "Enter the total life of the asset (in months)",
            "type": "number",
            "example": 60
          },
          "salvage_value": {
            "description": "Enter the remaining amount of the asset after its useful life or the value after it\u2019s fully depreciated",
            "type": "string",
            "format": "double",
            "example": 100
          },
          "depreciation_start_date": {
            "description": "Enter the depreciation start date of the asset",
            "type": "string",
            "example": "2024-12-17"
          },
          "description": {
            "description": "Description of the asset",
            "type": "string"
          },
          "asset_cost": {
            "description": "Enter the purchase cost of the asset",
            "type": "number",
            "format": "double",
            "example": 1000
          },
          "computation_type": {
            "description": "Enter the computation types of assets. The available types are: <code>prorata_basis</code> and <code>no_prorata</code>",
            "type": "string",
            "example": "prorata_basis"
          },
          "warranty_expiry_date": {
            "description": "Enter the warranty expiration date of the asset",
            "type": "string",
            "example": "2027-12-17"
          },
          "asset_purchase_date": {
            "description": "Enter the Purchase date of the asset",
            "type": "string",
            "example": "2024-12-17"
          },
          "serial_no": {
            "description": "serial number of the asset",
            "type": "string",
            "example": "SN-0001"
          },
          "dep_start_value": {
            "description": "Enter the value from which depreciation will be calculated.",
            "type": "number",
            "format": "double",
            "example": 1000
          },
          "notes": {
            "description": "Provide notes for the asset, if required",
            "type": "string",
            "example": "This is a laptop Model 2024"
          },
          "tags": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "tag_id": {
                  "description": "ID of the tag",
                  "type": "string",
                  "example": "460000000094001"
                },
                "tag_option_id": {
                  "description": "Option ID of the tag",
                  "type": "string",
                  "example": "460000000048001"
                }
              }
            }
          },
          "custom_fields": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "customfield_id": {
                  "description": "ID of the Custom Field",
                  "type": "string",
                  "example": "460000000098001"
                },
                "value": {
                  "description": "Value of the Custom Field",
                  "type": "string",
                  "example": "Normal"
                }
              }
            }
          }
        }
      }
    }
  }
}
