http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "required": [
          "name",
          "rate"
        ],
        "type": "object",
        "properties": {
          "name": {
            "description": "Name of the item. Max-length [100]",
            "type": "string",
            "example": "Hard Drive"
          },
          "rate": {
            "description": "Price of the item.",
            "type": "number",
            "format": "double",
            "example": 120
          },
          "description": {
            "description": "Description for the item. Max-length [2000]",
            "type": "string",
            "example": "500GB"
          },
          "tax_id": {
            "description": "ID of the tax to be associated to the item.",
            "type": "string",
            "example": 982000000037049,
            "x-node_available_in": [],
            "x-node_unavailable_in": [
              "us",
              "in"
            ]
          },
          "purchase_tax_rule_id": {
            "description": "Id of the purchase tax rule",
            "type": "string",
            "example": 127919000000106780,
            "x-node_available_in": [
              "mx",
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "sales_tax_rule_id": {
            "description": "Id of the sales tax rule",
            "type": "string",
            "example": 127919000000106780,
            "x-node_available_in": [
              "mx",
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "tax_percentage": {
            "description": "Percent of the tax.",
            "type": "string",
            "example": "70%"
          },
          "hsn_or_sac": {
            "description": "HSN Code",
            "type": "string",
            "x-node_available_in": [
              "in",
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "sat_item_key_code": {
            "description": "Add SAT Item Key Code for your goods/services. Download the <a href= http://omawww.sat.gob.mx/tramitesyservicios/Paginas/documentos/catCFDI_V_4_07122022.xls  >CFDI Catalogs.</a>",
            "type": "string",
            "x-node_available_in": [
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "unitkey_code": {
            "description": "Add Unit Key Code for your goods/services. Download the <a href= http://omawww.sat.gob.mx/tramitesyservicios/Paginas/documentos/catCFDI_V_4_07122022.xls  >CFDI Catalogs.</a>",
            "type": "string",
            "x-node_available_in": [
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "sku": {
            "description": "SKU value of item,should be unique throughout the product",
            "type": "string",
            "example": "s12345"
          },
          "product_type": {
            "description": "Specify the type of an item. Allowed values:<br/><code> goods</code> or <code> service</code> or <code> digital_service</code>. <br/><b>For SouthAfrica Edition:</b> <code>service</code>, <code>goods</code>, <code>capital_service</code> and <code>capital_goods</code>",
            "type": "string",
            "example": "goods"
          },
          "is_taxable": {
            "description": "Boolean to track the taxability of the item.",
            "type": "boolean",
            "x-node_available_in": [
              "in",
              "us",
              "mx",
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "tax_exemption_id": {
            "description": "ID of the tax exemption. Mandatory, if <code>is_taxable</code> is false.",
            "type": "string",
            "x-node_available_in": [
              "in",
              "us",
              "mx",
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "purchase_tax_exemption_id": {
            "description": "ID of the purchase tax exemption. Mandatory, if <code>is_taxable</code> is false.",
            "type": "string",
            "x-node_available_in": [
              "ke",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "account_id": {
            "description": "ID of the account to which the item has to be associated with.",
            "type": "string",
            "example": " "
          },
          "avatax_tax_code": {
            "description": "A tax code is a unique label used to group Items (products, services, or charges) together. Max-length [25]",
            "type": "string",
            "example": 982000000037049,
            "x-node_available_in": [
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          },
          "avatax_use_code": {
            "description": "Used to group like customers for exemption purposes. It is a custom value that links customers to a tax rule. Select from Avalara [standard codes][1] or enter a custom code. Max-length [25]",
            "type": "string",
            "example": 982000000037049,
            "x-node_available_in": [
              "Avalara Integration"
            ],
            "x-node_unavailable_in": []
          },
          "item_type": {
            "description": "Type of the item. Allowed values: <code>sales</code>,<code>purchases</code>,<code>sales_and_purchases</code> and <code>inventory</code>. Default value will be <code>sales</code>.",
            "type": "string",
            "example": " "
          },
          "purchase_description": {
            "description": "Purchase description for the item.",
            "type": "string",
            "example": " "
          },
          "purchase_rate": {
            "description": "Purchase price of the item.",
            "type": "string",
            "example": " "
          },
          "purchase_account_id": {
            "description": "ID of the COGS account to which the item has to be associated with. Mandatory, if item_type is purchase / sales and purchase / inventory.",
            "type": "string",
            "example": " "
          },
          "inventory_account_id": {
            "description": "ID of the stock account to which the item has to be associated with. Mandatory, if item_type is inventory.",
            "type": "string",
            "example": " "
          },
          "vendor_id": {
            "description": "Preferred vendor ID.",
            "type": "string",
            "example": " "
          },
          "reorder_level": {
            "description": "Reorder level of the item.",
            "type": "string",
            "example": " "
          },
          "locations": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "location_id": {
                  "description": "Location ID",
                  "type": "string",
                  "example": "460000000038080"
                },
                "initial_stock": {
                  "description": "Opening stock of the item.",
                  "type": "string",
                  "example": " "
                },
                "initial_stock_rate": {
                  "description": "Unit price of the opening stock.",
                  "type": "string",
                  "example": " "
                }
              }
            }
          },
          "item_tax_preferences": {
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "tax_id": {
                  "description": "ID of the tax to be associated to the item.",
                  "type": "string",
                  "example": 982000000037049,
                  "x-node_available_in": [
                    "in"
                  ],
                  "x-node_unavailable_in": []
                },
                "tax_specification": {
                  "description": "Set whether the tax type is intra/interstate",
                  "type": "string",
                  "example": "intra",
                  "x-node_available_in": [
                    "in"
                  ],
                  "x-node_unavailable_in": []
                }
              }
            },
            "x-node_available_in": [
              "in"
            ],
            "x-node_unavailable_in": []
          },
          "custom_fields": {
            "description": "Custom fields for an item.",
            "type": "array",
            "items": {
              "type": "object",
              "properties": {
                "customfield_id": {
                  "type": "integer",
                  "format": "int64",
                  "example": "46000000012845"
                },
                "value": {
                  "description": "Value of the Custom Field",
                  "type": "string",
                  "example": "Normal"
                }
              }
            }
          }
        }
      }
    }
  }
}
