http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "tax_id": {
            "description": "ID of the Tax",
            "type": "string",
            "example": "982000000566009"
          },
          "tax_name": {
            "description": "Name of the Tax",
            "type": "string",
            "example": "Sales Group"
          },
          "tax_percentage": {
            "description": "Number of Percentage Taxable.",
            "type": "number",
            "format": "double",
            "example": 10.5
          },
          "tax_type": {
            "description": "Type to determine whether it is a simple or compound tax. <code>Allowed Values: tax and compound_tax</code>",
            "type": "string",
            "example": "tax"
          },
          "tax_factor": {
            "description": "Type of Tax Factor. Allowed values: <code>rate</code>, <code>share</code>",
            "type": "string",
            "example": "rate",
            "x-node_available_in": [
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "tax_specific_type": {
            "description": "Type of Tax For <b>Indian</b> Edition. Allowed Values : <code>igst</code> ,<code>cgst</code> ,<code>sgst</code> ,<code>nil</code>, <code> cess </code>.</br>Type of Tax for <b>Mexico</b> Edition. Allowed Values : <code>isr</code>, <code>iva</code>, <code>ieps</code>.</br>Type of Tax for <b>South Africa</b> Edition. Allowed Values : </br><code>soa_less_than_28d</code> - Supply of accommodation not exceeding 28 days, </br><code>soa_more_than_28d</code> - Supply of accommodation exceeding than 28 days, </br><code>ciu_prev_tax_supply</code> - Change in use (Taxable supplies), </br><code>ciu_prev_nontax_supply</code> - Change in use (Non-Taxable supplies), </br><code>export_of_shg</code> - Export of second hand goods.",
            "type": "string",
            "x-node_available_in": [
              "in",
              "mx",
              "za"
            ],
            "x-node_unavailable_in": []
          },
          "tax_authority_name": {
            "description": "Name of the Tax Authority",
            "type": "string",
            "example": "Illinois Department of Revenue"
          },
          "tax_authority_id": {
            "description": "ID of the tax authority. Tax authority depends on the location of the customer. For example, if the customer is located in NY, then the tax authority is NY tax authority.",
            "type": "string",
            "example": "460000000066001",
            "x-node_available_in": [
              "us",
              "mx"
            ],
            "x-node_unavailable_in": []
          },
          "country_code": {
            "description": "Two letter country code for the EU country to which the tax belongs.",
            "type": "string",
            "example": "UK",
            "x-node_available_in": [
              "uk",
              "eu",
              "gcc",
              "global"
            ],
            "x-node_unavailable_in": []
          },
          "purchase_tax_expense_account_id": {
            "description": "Account ID in which Purchase Tax will be Computed",
            "type": "integer",
            "format": "int64",
            "x-node_available_in": [
              "au",
              "ca"
            ],
            "x-node_unavailable_in": []
          },
          "is_value_added": {
            "description": "Check if Tax is Value Added",
            "type": "boolean",
            "example": false
          },
          "update_recurring_invoice": {
            "description": "Check if recurring invoice should be updated",
            "type": "boolean",
            "example": false
          },
          "update_recurring_expense": {
            "description": "Check if recurring expenses should be updated",
            "type": "boolean",
            "example": false
          },
          "update_draft_invoice": {
            "description": "Check if Draft Invoices should be updated",
            "type": "boolean",
            "example": false
          },
          "update_recurring_bills": {
            "description": "Check if Recurring Bills should be updated",
            "type": "boolean",
            "example": false
          },
          "update_draft_so": {
            "description": "Check if Draft Sales Orders should be updated",
            "type": "boolean",
            "example": false
          },
          "update_subscription": {
            "description": "Check if Subscriptions should be updated",
            "type": "boolean",
            "example": false
          },
          "update_project": {
            "description": "Check if Projects should be updated",
            "type": "boolean",
            "example": false
          },
          "is_editable": {
            "description": "Check if tax is editable",
            "type": "boolean",
            "example": true
          },
          "tds_payable_account_id": {
            "description": "Input Tax ID. The amount of money charged to you as Tax on your purchases.",
            "type": "string",
            "example": "132086000000107337",
            "x-node_available_in": [
              "mx"
            ],
            "x-node_unavailable_in": []
          }
        }
      }
    }
  }
}
