http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "accessToken": {
            "type": "string",
            "description": "The access token to be revoked."
          },
          "clientId": {
            "type": "string",
            "description": "The client ID associated with the access token."
          },
          "clientSecret": {
            "type": "string",
            "description": "The client secret associated with the access token."
          }
        },
        "required": [
          "accessToken",
          "clientId",
          "clientSecret"
        ]
      }
    }
  },
  "required": true
}
