http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "labelId": {
            "type": "integer",
            "description": "Data classification label id for team",
            "format": "int64",
            "example": 3000457366756291000
          },
          "notClassifiedOnly": {
            "type": "boolean",
            "description": "Assign data classification label to not-classified only or to all boards of team",
            "example": true
          }
        }
      }
    }
  },
  "required": true
}
