http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "fillColor": {
            "type": "string",
            "description": "Fill color for the tag.",
            "enum": [
              "red",
              "light_green",
              "cyan",
              "yellow",
              "magenta",
              "green",
              "blue",
              "gray",
              "violet",
              "dark_green",
              "dark_blue",
              "black"
            ]
          },
          "title": {
            "type": "string",
            "description": "Text of the tag. Case-sensitive. Must be unique.",
            "example": "done",
            "maxLength": 120,
            "minLength": 0
          }
        }
      }
    }
  },
  "required": true
}
