http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "team": {
            "allOf": [
              {
                "type": "object",
                "properties": {
                  "id": {
                    "type": "string",
                    "readOnly": true
                  },
                  "summary": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                  },
                  "type": {
                    "type": "string",
                    "readOnly": true,
                    "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                  },
                  "self": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "format": "url",
                    "description": "the API show URL at which the object is accessible"
                  },
                  "html_url": {
                    "type": "string",
                    "nullable": true,
                    "readOnly": true,
                    "format": "url",
                    "description": "a URL at which the entity is uniquely displayed in the Web app"
                  }
                }
              },
              {
                "type": "object",
                "properties": {
                  "type": {
                    "type": "string",
                    "description": "The type of object being created.",
                    "default": "team",
                    "enum": [
                      "team"
                    ]
                  },
                  "name": {
                    "type": "string",
                    "description": "The name of the team.",
                    "maxLength": 100
                  },
                  "description": {
                    "type": "string",
                    "description": "The description of the team.",
                    "maxLength": 1024
                  },
                  "default_role": {
                    "type": "string",
                    "description": "The team is private if the value is \"none\", or public if it is \"manager\" (the default permissions for a non-member of the team are either \"none\", or their base role up until \"manager\").",
                    "default": "manager",
                    "enum": [
                      "manager",
                      "none"
                    ]
                  }
                },
                "required": [
                  "name",
                  "type"
                ],
                "example": {
                  "type": "team",
                  "name": "Engineering",
                  "description": "The engineering team"
                }
              }
            ]
          }
        },
        "required": [
          "team"
        ]
      },
      "examples": {
        "request": {
          "summary": "Request Example",
          "value": {
            "team": {
              "type": "team",
              "name": "Engineering",
              "description": "The engineering team"
            }
          }
        }
      }
    }
  },
  "description": "The team to be created."
}
