http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "subscribers": {
            "type": "array",
            "uniqueItems": true,
            "minItems": 1,
            "items": {
              "title": "NotificationSubscriber",
              "description": "A reference of a subscriber entity.",
              "type": "object",
              "properties": {
                "subscriber_id": {
                  "type": "string",
                  "description": "The ID of the entity being subscribed"
                },
                "subscriber_type": {
                  "type": "string",
                  "description": "The type of the entity being subscribed",
                  "enum": [
                    "user",
                    "team"
                  ]
                }
              },
              "example": {
                "subscriber_id": "PD1234",
                "subscriber_type": "user"
              }
            }
          }
        },
        "required": [
          "subscribers"
        ]
      },
      "examples": {
        "request": {
          "summary": "Request Example",
          "value": {
            "subscribers": [
              {
                "subscriber_id": "PD1234",
                "subscriber_type": "team"
              },
              {
                "subscriber_id": "PD1235",
                "subscriber_type": "team"
              },
              {
                "subscriber_id": "PD1234",
                "subscriber_type": "user"
              }
            ]
          }
        }
      }
    }
  },
  "description": "The entities to subscribe."
}
