http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "action": {
            "oneOf": [
              {
                "allOf": [
                  {
                    "type": "object",
                    "properties": {
                      "name": {
                        "type": "string",
                        "example": "Restart apache",
                        "maxLength": 255
                      },
                      "description": {
                        "type": "string",
                        "example": "Restarts apache on the us-west-2-shopping-cart host",
                        "maxLength": 1024
                      },
                      "action_classification": {
                        "type": "string",
                        "enum": [
                          "diagnostic",
                          "remediation"
                        ],
                        "nullable": true
                      },
                      "action_type": {
                        "type": "string",
                        "enum": [
                          "script",
                          "process_automation"
                        ],
                        "example": "process_automation"
                      },
                      "runner": {
                        "type": "string",
                        "maxLength": 36
                      },
                      "only_invocable_on_unresolved_incidents": {
                        "type": "boolean",
                        "description": "If true, the action can only be invoked against an unresolved incident.",
                        "default": false,
                        "example": false
                      },
                      "allow_invocation_manually": {
                        "type": "boolean",
                        "description": "If true, the action can only be invoked manually by a user.",
                        "default": true,
                        "example": true
                      },
                      "allow_invocation_from_event_orchestration": {
                        "type": "boolean",
                        "description": "If true, the action can only be invoked automatically by an Event Orchestration.",
                        "default": true,
                        "example": true
                      },
                      "map_to_all_services": {
                        "type": "boolean",
                        "description": "If true, the action will be associated with every service.",
                        "default": false,
                        "example": false
                      }
                    }
                  },
                  {
                    "type": "object",
                    "properties": {
                      "action_data_reference": {
                        "type": "object",
                        "properties": {
                          "script": {
                            "type": "string",
                            "description": "Body of the script to be executed on the Runner. To execute it, the Runner will write the content of the property into a temp file, make the file executable and execute it. It is assumed that the Runner has a properly configured environment to run the script as an executable file. This behaviour can be altered by providing the `invocation_command` property. The maxLength value is specified in bytes.",
                            "example": "print(\"Hello from a Python script!\")",
                            "maxLength": 16777215
                          },
                          "invocation_command": {
                            "type": "string",
                            "description": "The command to executed a script with. With the body of the script written into a temp file, the Runner will execute the `<invocation_command> <temp_file>` command. The maxLength value is specified in bytes.",
                            "example": "/usr/local/bin/python3",
                            "maxLength": 65535
                          }
                        },
                        "required": [
                          "script"
                        ]
                      }
                    }
                  }
                ]
              },
              {
                "allOf": [
                  {
                    "type": "object",
                    "properties": {
                      "name": {
                        "type": "string",
                        "example": "Restart apache",
                        "maxLength": 255
                      },
                      "description": {
                        "type": "string",
                        "example": "Restarts apache on the us-west-2-shopping-cart host",
                        "maxLength": 1024
                      },
                      "action_classification": {
                        "type": "string",
                        "enum": [
                          "diagnostic",
                          "remediation"
                        ],
                        "nullable": true
                      },
                      "action_type": {
                        "type": "string",
                        "enum": [
                          "script",
                          "process_automation"
                        ],
                        "example": "process_automation"
                      },
                      "runner": {
                        "type": "string",
                        "maxLength": 36
                      },
                      "only_invocable_on_unresolved_incidents": {
                        "type": "boolean",
                        "description": "If true, the action can only be invoked against an unresolved incident.",
                        "default": false,
                        "example": false
                      },
                      "allow_invocation_manually": {
                        "type": "boolean",
                        "description": "If true, the action can only be invoked manually by a user.",
                        "default": true,
                        "example": true
                      },
                      "allow_invocation_from_event_orchestration": {
                        "type": "boolean",
                        "description": "If true, the action can only be invoked automatically by an Event Orchestration.",
                        "default": true,
                        "example": true
                      },
                      "map_to_all_services": {
                        "type": "boolean",
                        "description": "If true, the action will be associated with every service.",
                        "default": false,
                        "example": false
                      }
                    }
                  },
                  {
                    "type": "object",
                    "properties": {
                      "action_data_reference": {
                        "type": "object",
                        "properties": {
                          "process_automation_job_id": {
                            "type": "string",
                            "example": "79c199bba1aff6e519f198457f5ec0fc",
                            "maxLength": 36
                          },
                          "process_automation_job_arguments": {
                            "type": "string",
                            "description": "Arguments to pass to the Process Automation job. The maxLength value is specified in bytes.",
                            "example": "-env production",
                            "maxLength": 1024
                          },
                          "process_automation_node_filter": {
                            "type": "string",
                            "description": "Node filter for the Process Automation job. The maxLength value is specified in bytes. Filter syntax: https://docs.rundeck.com/docs/manual/11-node-filters.html#node-filter-syntax",
                            "example": "mynode1 !nodename: mynode2",
                            "maxLength": 1024
                          }
                        },
                        "required": [
                          "process_automation_job_id"
                        ]
                      }
                    }
                  }
                ]
              }
            ],
            "discriminator": {
              "propertyName": "action_type",
              "mapping": {
                "script": "#/components/schemas/AutomationActionsScriptActionPutBody",
                "process_automation": "#/components/schemas/AutomationActionsProcessAutomationJobActionPutBody"
              }
            }
          }
        },
        "required": [
          "action"
        ]
      }
    }
  },
  "required": true
}
