http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "properties": {
          "action": {
            "oneOf": [
              {
                "allOf": [
                  {
                    "type": "object",
                    "properties": {
                      "name": {
                        "type": "string",
                        "example": "Restart apache",
                        "maxLength": 255
                      },
                      "description": {
                        "type": "string",
                        "example": "Restarts apache on the us-west-2-shopping-cart host",
                        "maxLength": 1024
                      },
                      "action_classification": {
                        "type": "string",
                        "enum": [
                          "diagnostic",
                          "remediation"
                        ],
                        "nullable": true
                      },
                      "action_type": {
                        "type": "string",
                        "enum": [
                          "script",
                          "process_automation"
                        ],
                        "example": "process_automation"
                      },
                      "runner": {
                        "type": "string",
                        "example": "1a6763bd-b1ad-458f-a347-6c8a9bea2d70",
                        "maxLength": 36
                      },
                      "services": {
                        "nullable": false,
                        "type": "array",
                        "items": {
                          "allOf": [
                            {
                              "allOf": [
                                {
                                  "type": "object",
                                  "properties": {
                                    "id": {
                                      "type": "string",
                                      "readOnly": true
                                    },
                                    "summary": {
                                      "type": "string",
                                      "nullable": true,
                                      "readOnly": true,
                                      "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                                    },
                                    "type": {
                                      "type": "string",
                                      "readOnly": true,
                                      "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                                    },
                                    "self": {
                                      "type": "string",
                                      "nullable": true,
                                      "readOnly": true,
                                      "format": "url",
                                      "description": "the API show URL at which the object is accessible"
                                    },
                                    "html_url": {
                                      "type": "string",
                                      "nullable": true,
                                      "readOnly": true,
                                      "format": "url",
                                      "description": "a URL at which the entity is uniquely displayed in the Web app"
                                    }
                                  }
                                },
                                {
                                  "type": "object",
                                  "required": [
                                    "type",
                                    "id"
                                  ]
                                }
                              ]
                            },
                            {
                              "type": "object",
                              "properties": {
                                "type": {
                                  "type": "string",
                                  "enum": [
                                    "service_reference"
                                  ]
                                }
                              }
                            }
                          ]
                        }
                      },
                      "teams": {
                        "nullable": false,
                        "type": "array",
                        "items": {
                          "allOf": [
                            {
                              "allOf": [
                                {
                                  "type": "object",
                                  "properties": {
                                    "id": {
                                      "type": "string",
                                      "readOnly": true
                                    },
                                    "summary": {
                                      "type": "string",
                                      "nullable": true,
                                      "readOnly": true,
                                      "description": "A short-form, server-generated string that provides succinct, important information about an object suitable for primary labeling of an entity in a client. In many cases, this will be identical to `name`, though it is not intended to be an identifier."
                                    },
                                    "type": {
                                      "type": "string",
                                      "readOnly": true,
                                      "description": "A string that determines the schema of the object. This must be the standard name for the entity, suffixed by `_reference` if the object is a reference."
                                    },
                                    "self": {
                                      "type": "string",
                                      "nullable": true,
                                      "readOnly": true,
                                      "format": "url",
                                      "description": "the API show URL at which the object is accessible"
                                    },
                                    "html_url": {
                                      "type": "string",
                                      "nullable": true,
                                      "readOnly": true,
                                      "format": "url",
                                      "description": "a URL at which the entity is uniquely displayed in the Web app"
                                    }
                                  }
                                },
                                {
                                  "type": "object",
                                  "required": [
                                    "type",
                                    "id"
                                  ]
                                }
                              ]
                            },
                            {
                              "type": "object",
                              "properties": {
                                "type": {
                                  "type": "string",
                                  "enum": [
                                    "team_reference"
                                  ]
                                }
                              }
                            }
                          ]
                        }
                      },
                      "only_invocable_on_unresolved_incidents": {
                        "type": "boolean",
                        "description": "If true, the action can only be invoked against an unresolved incident.",
                        "default": false,
                        "example": false
                      },
                      "allow_invocation_manually": {
                        "type": "boolean",
                        "description": "If true, the action can only be invoked manually by a user.",
                        "default": true,
                        "example": true
                      },
                      "allow_invocation_from_event_orchestration": {
                        "type": "boolean",
                        "description": "If true, the action can only be invoked automatically by an Event Orchestration.",
                        "default": true,
                        "example": true
                      },
                      "map_to_all_services": {
                        "type": "boolean",
                        "description": "If true, the action will be associated with every service.",
                        "default": false,
                        "example": false
                      }
                    },
                    "required": [
                      "name",
                      "description",
                      "action_type"
                    ]
                  },
                  {
                    "type": "object",
                    "properties": {
                      "action_data_reference": {
                        "type": "object",
                        "properties": {
                          "script": {
                            "type": "string",
                            "description": "Body of the script to be executed on the Runner. To execute it, the Runner will write the content of the property into a temp file, make the file executable and execute it. It is assumed that the Runner has a properly configured environment to run the script as an executable file. This behaviour can be altered by providing the `invocation_command` property. The maxLength value is specified in bytes.",
                            "example": "print(\"Hello from a Python script!\")",
                            "maxLength": 16777215
                          },
                          "invocation_command": {
                            "type": "string",
                            "description": "The command to executed a script with. With the body of the script written into a temp file, the Runner will execute the `<invocation_command> <temp_file>` command. The maxLength value is specified in bytes.",
                            "example": "/usr/local/bin/python3",
                            "maxLength": 65535
                          }
                        },
                        "required": [
                          "script"
                        ]
                      }
                    },
                    "required": [
                      "action_data_reference"
                    ]
                  }
                ]
              },
              {
