          "value": {
            "action": {
              "name": "Restart apache",
              "description": "Restarts apache on the us-west-2-shopping-cart host",
              "action_type": "script",
              "action_data_reference": {
                "script": "java --version"
              },
              "teams": [
                {
                  "id": "PQ9K7I8",
                  "type": "team_reference"
                }
              ],
              "services": [
