http_endpoint.request_body_spec:
{
  "required": true,
  "description": "An object containing the fields to POST for the request.\n\nSee the corresponding object definition for field details.",
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "description": "Represents a [PublishScheduledShift](api-endpoint:Labor-PublishScheduledShift) request.",
        "x-release-status": "BETA",
        "required": [
          "idempotency_key"
        ],
        "properties": {
          "idempotency_key": {
            "type": "string",
            "description": "A unique identifier for the `PublishScheduledShift` request, used to ensure the\n[idempotency](https://developer.squareup.com/docs/build-basics/common-api-patterns/idempotency)\nof the operation.",
            "minLength": 1,
            "maxLength": 128
          },
          "version": {
            "type": "integer",
            "description": "The current version of the scheduled shift, used to enable [optimistic concurrency](https://developer.squareup.com/docs/build-basics/common-api-patterns/optimistic-concurrency)\ncontrol. If the provided version doesn't match the server version, the request fails.\nIf omitted, Square executes a blind write, potentially overwriting data from another publish request."
          },
          "scheduled_shift_notification_audience": {
            "type": "string",
            "enum": [
              "ALL",
              "AFFECTED",
              "NONE"
            ],
            "x-enum-elements": [
              {
                "name": "ALL",
                "description": "Notify all active team members. Note that republishing an unchanged\nshift with this setting resends the notification."
              },
              {
                "name": "AFFECTED",
                "description": "Notify the team member assigned to the shift. If the shift assignment\nchanges, notify both the newly assigned and previously assigned team members."
              },
              {
                "name": "NONE",
                "description": "Do not send a notification."
              }
            ],
            "description": "Indicates whether Square sends an email notification to team members\nwhen a scheduled shift is published and which team members receive the notification.",
            "x-release-status": "BETA"
          }
        },
        "example": {
          "idempotency_key": "HIDSNG5KS478L",
          "scheduled_shift_notification_audience": "ALL",
          "version": 2
        }
      }
    }
  }
}
