http_endpoint.request_body_spec:
{
  "required": true,
  "content": {
    "application/json": {
      "schema": {
        "type": "object",
        "description": "Input schema for updating an existing project",
        "properties": {
          "name": {
            "type": "string",
            "description": "Name of the project"
          },
          "color": {
            "type": "string",
            "description": "Color of the project - hex color code"
          },
          "sortOrder": {
            "type": "integer",
            "format": "int64",
            "description": "Sort order value - default 0"
          },
          "viewMode": {
            "type": "string",
            "enum": [
              "list",
              "kanban",
              "timeline"
            ],
            "description": "View mode - how the project is displayed"
          },
          "kind": {
            "type": "string",
            "enum": [
              "TASK",
              "NOTE"
            ],
            "description": "Project kind - TASK or NOTE"
          }
        }
      },
      "example": {
        "name": "Project Name",
        "color": "#F18181",
        "viewMode": "list",
        "kind": "TASK"
      }
    }
  }
}
