http_endpoint.request_body_spec:
{
  "content": {
    "application/json": {
      "schema": {
        "oneOf": [
          {
            "type": "object",
            "properties": {
              "value": {
                "type": "object",
                "description": "An object containing the key and value to set for the card's Custom Field value. The key used to set the value should match the type of Custom Field defined.",
                "properties": {
                  "text": {
                    "type": "string"
                  },
                  "checked": {
                    "type": "boolean"
                  },
                  "date": {
                    "type": "string",
                    "format": "date-time",
                    "example": "2018-03-13T16:00:00.000Z"
                  },
                  "number": {
                    "type": "number"
                  }
                }
              }
            }
          },
          {
            "type": "object",
            "properties": {
              "idValue": {
                "type": "string",
                "pattern": "^[0-9a-fA-F]{24}$",
                "example": "5abbe4b7ddc1b351ef961414"
              }
            }
          }
        ]
      }
    }
  }
}
