def time_interval_to_hms_range(
    duration_tuple: tuple[float, float]  # A tuple of (start_seconds, end_seconds) as floats
) -> str:  # Formatted timestamp range string in [HH:MM:SS.ss]-[HH:MM:SS.ss] format
    """Convert a time interval tuple (start_seconds, end_seconds) to HMS timestamp range format."""
    def seconds_to_timestamp(
        total_seconds: float  # Number of seconds to convert to timestamp
    ) -> str:  # Formatted timestamp string in HH:MM:SS.ss format
        """Convert seconds to HH:MM:SS.ss format."""
        hours = int(total_seconds // 3600)
        minutes = int((total_seconds % 3600) // 60)
        seconds = total_seconds % 60
        
        # Format with 2 decimal places for fractional seconds
        return f"{hours:02d}:{minutes:02d}:{seconds:05.2f}"
    
    start_seconds, end_seconds = duration_tuple
    start_timestamp = seconds_to_timestamp(start_seconds)
    end_timestamp = seconds_to_timestamp(end_seconds)
    
    return f"[{start_timestamp}]-[{end_timestamp}]"
