# this file is for loading the new binary memory image formats generated by FB2 for the
# note that for memories that have more than 64b per word, the returned LSB, MSBs are interleaved

import warnings
import numpy as np


def load_binfile(filename):
    with warnings.catch_warnings():
        # the rqueue contents may be empty
        warnings.filterwarnings("ignore", message=".*input contained no data.*")
        vals = np.fromfile(filename, dtype=np.uint64)
    return np.atleast_1d(vals)
