from gtts import gTTS

def GTTS(Text: str, lang: str, tld: str, location="TTS.mp3"):
    '''
    # save file
    GTTS("Hello gooflo", "en", "com.au")
    '''
    tts = gTTS(Text, lang=lang, tld=tld)
    tts.save(location)

