import pytest

from intuned_browser.helpers import sanitize_html


@pytest.mark.asyncio
async def test_sanitize_html_basic():
    html = "<div>Hello</div>"
    assert sanitize_html(html) == "<div>Hello</div>"


@pytest.mark.asyncio
async def test_sanitize_html_with_options():
    html = """<div>
        <script>alert('test')</script>
        <style>.test { color: red; }</style>
        <p>Content</p>
    </div>"""

    # Test keeping styles but removing scripts
    result = sanitize_html(html, remove_scripts=True, remove_styles=False)
    assert "<script>" not in result
    assert "<style>" in result
    assert "Content" in result


@pytest.mark.asyncio
async def test_sanitize_html_attribute_control():
    html = """<div data-long="{'x' * 600}" class="test" style="color: red;">Content</div>"""

    # Test custom attribute preservation
    result = sanitize_html(html, max_attribute_length=10, preserve_attributes=["class", "style"])
    assert 'class="test"' in result
    assert 'style="color: red;"' in result
    assert "data-long" not in result


