@pytest.mark.asyncio
async def test_sanitize_html_remove_scripts_option():
    html = """<div><script>alert('xss')</script><p>Content</p></div>"""

    # Test with remove_scripts=True (default)
    result_remove = sanitize_html(html, remove_scripts=True)
    assert "<script>" not in result_remove
    assert "Content" in result_remove

    # Test with remove_scripts=False
    result_keep = sanitize_html(html, remove_scripts=False)
    assert "<script>" in result_keep
    assert "Content" in result_keep


