@pytest.mark.asyncio
async def test_sanitize_html_max_attribute_length_option():
    html = """<div data-long="this-is-a-very-long-attribute-value" data-short="ok" class="test">Content</div>"""

    # Test with max_attribute_length=10
    result = sanitize_html(html, max_attribute_length=10, preserve_attributes=["class", "data-short"])
    assert "data-long" not in result  # Should be removed (too long)
    assert "data-short" in result  # Should be kept (short enough)
    assert "class" in result  # Should be kept (preserved)
    assert "Content" in result


@pytest.mark.asyncio
async def test_sanitize_html_preserve_attributes_option():
    html = """<div id="test" class="container" data-value="1234567890" style="color: red;">Content</div>"""

    # Test with custom preserve_attributes
    result = sanitize_html(
        html, preserve_attributes=["id", "class", "data-value"], max_attribute_length=7
    )  # data-value should be removed because it's too long, but its not because it's in the preserve_attributes list
    assert 'id="test"' in result
    assert 'class="container"' in result
    assert "data-value" in result  # Should be removed
    assert "style" not in result  # Should be removed
    assert "Content" in result


