@pytest.mark.asyncio
async def test_sanitize_html_minify_whitespace_option():
    html = """<div>

    <p>   Content   </p>
    </div>"""

    # Test with minify_whitespace=True (default)
    print("BEFORE:", html)
    result_minify = sanitize_html(html, minify_whitespace=True)
    print("AFTER:", result_minify)
    assert "\n\n" not in result_minify
    assert "   " not in result_minify
    assert "Content" in result_minify

    # Test with minify_whitespace=False
    result_keep = sanitize_html(html, minify_whitespace=False)
    assert "Content" in result_keep
    # Original whitespace should be more preserved
