from enum import StrEnum, auto, Enum
class AutoLower(StrEnum):
    """
    StrEnum contrary to simple Enum is of type `str`, so it can be used as a string.
    StrEnum whose `auto()  # type: ignore` values are lower-case.
    (Identical to StrEnum's own default, but keeps naming symmetrical.)
    """
    @staticmethod
    def _generate_next_value_(name, start, count, last_values):
        return name.lower()            # StrEnum already does this

class AutoUpper(StrEnum):
    """
    StrEnum contrary to simple Enum is of type `str`, so it can be used as a string.
    StrEnum whose `auto()  # type: ignore` values stay as-is (UPPER_CASE).
    """
    @staticmethod
    def _generate_next_value_(name, start, count, last_values):
        return name                    # keep original upper-case




class ActionTrigger(AutoLower):
    MANUAL = auto()  # type: ignore
    SCHEDULER =  auto()  # type: ignore
    SCHEDULER_MAIN = auto()  # type: ignore
    SCHEDULER_FALLBACK = auto()  # type: ignore
    SCHEDULER_RETRY = auto()  # type: ignore
    SCHEDULER_VERIFICATION = auto()  # type: ignore
    EVENT_GCS_UPLOAD= auto()  # type: ignore
    EVENT_PUBSUB= auto()  # type: ignore
    ANOTHER_PIPELINE = auto()  # type: ignore
    THRESHOLD_BASED = auto()  # type: ignore
    DRIFT_DETECTION_BASED = auto()  # type: ignore


