    RESOURCE_NOT_FOUND = 2202
    TABLE_NOT_FOUND = 2205
    TABLES_NOT_FOUND = 2206
    TABLE_EMPTY = 2210
    DB_NOT_FOUND = 2215
    COLLECTION_NOT_FOUND = 2220
