    SYSTEM_MEMORY_USAGE_HIGH = 3100
    SYSTEM_CPU_USAGE_HIGH = 3110
    SYSTEM_NETWORK_SPEED_SLOW = 3120
    SYSTEM_DISK_SPACE_LOW = 3130
    SYSTEM_THROUGHPUT_LOW = 3140

