    SYSTEM_CONCURRENCY_LIMIT = 4100
    SYSTEM_CONNECTIONS_LIMIT = 4110
    SERVICE_RESPONSE_TIME_LIMIT = 4130

    # ERROR ALERTS (5000-5999)

