    SYSTEM_OUT_OF_MEMORY = 6000
    SYSTEM_CPU_LIMIT_REACHED = 6005
    SYSTEM_DISK_SPACE_FULL = 6010
    SYSTEM_NETWORK_UNAVAILABLE = 6015

