from enum import StrEnum, Enum, auto
class AutoLower(StrEnum):
    """
    StrEnum contrary to simple Enum is of type `str`, so it can be used as a string.
    StrEnum whose `auto()  # type: ignore` values are lower-case.
    (Identical to StrEnum's own default, but keeps naming symmetrical.)
    """
    @staticmethod
    def _generate_next_value_(name, start, count, last_values):
        return name.lower()            # StrEnum already does this

class AutoUpper(StrEnum):
    """
    StrEnum contrary to simple Enum is of type `str`, so it can be used as a string.
    StrEnum whose `auto()  # type: ignore` values stay as-is (UPPER_CASE).
    """
    @staticmethod
    def _generate_next_value_(name, start, count, last_values):
        return name                    # keep original upper-case


class LogLevel(Enum):
    NOTSET=0
    DEBUG =10
    INFO = 20
    NOTICE= 25
    WARNING=30
    ERROR=40
    CRITICAL=50

    def __str__(self):
        return self.name

class LoggingHandler(AutoLower):

    """
    Standardized remote logging handlers for data engineering pipelines,
    designed for easy analysis and identification of remote logging
    requirements
    """
    
    NONE = auto()  # type: ignore  # No remote handler
    LOCAL_STREAM = auto()  # type: ignore  # Local stream handler
    GCP_CLOUD_LOGGING = auto()  # type: ignore
    GCP_ERROR_REPORTING = auto()  # type: ignore
    GCP_FIREBASE = auto()  # type: ignore
    AWS_CLOUD_WATCH = auto()  # type: ignore
    AZURE_MONITOR = auto()  # type: ignore
    AZURE_APPLICATION_INSIGHTS = auto()  # type: ignore
    IBM_LOG_ANALYTICS = auto()  # type: ignore
    ALIBABA_LOG_SERVICE = auto()  # type: ignore
    LOGGLY = auto()  # type: ignore
    DATADOG = auto()  # type: ignore
    SENTRY = auto()  # type: ignore
    SUMOLOGIC = auto()  # type: ignore
    # --- Other ---
    SYSLOG = auto()  # type: ignore # For system logs
    CUSTOM = auto()  # type: ignore # For a user-defined remote handler
    OTHER = auto()  # type: ignore

