#!/bin/bash

# ----------------------------------------------------------------------------------------------------------------------
# Upload-to-TestPyPI
# ----------------------------------------------------------------------------------------------------------------------
cd ..
# set -e
clear

# - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
# Configure TestPyPI repository
TESTPYPI_URL="https://test.pypi.org/legacy/"

# - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - -
# Build and upload
uv build
uv publish --publish-url "$TESTPYPI_URL" --token "$TESTPYPI_TOKEN"

# ----------------------------------------------------------------------------------------------------------------------
cd dev/
